"use client";

// ** import types
import type { ColumnDef, ColumnResizeMode, Row, ExpandedState } from "@tanstack/react-table";
import type { TableConfig } from "./utils/table-config";
import type { CaseFormatConfig } from "./utils/case-utils";
import type { DataTransformFunction, ExportableData } from "./utils/export-utils";

// ** import core packages
import {
  type ColumnSizingState,
  flexRender,
  getCoreRowModel,
  getExpandedRowModel,
  getFacetedRowModel,
  getFacetedUniqueValues,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  useReactTable,
} from "@tanstack/react-table";
import React, { useEffect, useCallback, useMemo, useRef, useState } from "react";
import { AlertCircle } from "lucide-react";

// ** import components
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Skeleton } from "@/components/ui/skeleton";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import { DataTablePagination } from "./pagination";
import { DataTableToolbar } from "./toolbar";
import { DataTableResizer } from "./data-table-resizer";

// ** import utils
import { cn } from "@/lib/utils";
import { useTableConfig } from "./utils/table-config";
import { useTableColumnResize } from "./hooks/use-table-column-resize";
import { preprocessSearch } from "./utils/search";
import {
  createSortingHandler,
  createColumnFiltersHandler,
  createColumnVisibilityHandler,
  createPaginationHandler,
  createColumnSizingHandler,
  createSortingState
} from "./utils/table-state-handlers";
import { createKeyboardNavigationHandler } from "./utils/keyboard-navigation";
import { createConditionalStateHook } from "./utils/conditional-state";
import {
  initializeColumnSizes,
  trackColumnResizing,
  cleanupColumnResizing
} from "./utils/column-sizing";

// Define types for the data fetching function params and result
interface DataFetchParams {
  page: number;
  limit: number;
  search: string;
  from_date: string;
  to_date: string;
  sort_by: string;
  sort_order: string;
}

interface DataFetchResult<TData> {
  success: boolean;
  data: TData[];
  pagination: {
    page: number;
    limit: number;
    total_pages: number;
    total_items: number;
  };
}

// Types for table handlers
type PaginationUpdater<TData> = (prev: { pageIndex: number; pageSize: number }) => { pageIndex: number; pageSize: number };
type SortingUpdater = (prev: { id: string; desc: boolean }[]) => { id: string; desc: boolean }[];
type ColumnOrderUpdater = (prev: string[]) => string[];
type RowSelectionUpdater = (prev: Record<string, boolean>) => Record<string, boolean>;

// Subrows configuration interface
export interface SubRowsConfig<TData> {
  // Enable subrows feature
  enabled: boolean;

  // Rendering mode
  mode: 'same-columns' | 'custom-columns' | 'custom-component';

  // Field name for accessing subrows in data (default: 'subRows')
  subRowsField?: string;

  // For custom-columns mode: different columns for subrows
  subRowColumns?: ColumnDef<any, unknown>[];
  showSubRowHeaders?: boolean;

  // For custom-component mode: custom component for subrows
  SubRowComponent?: React.ComponentType<{
    row: Row<TData>;
    data: TData;
  }>;

  // Hide expand icon when only 1 subrow
  hideExpandIconWhenSingle?: boolean;

  // Auto-expand rows with single subrow
  autoExpandSingle?: boolean;

  // Indentation size for subrows (in pixels)
  indentSize?: number;

  // Default expanded state
  defaultExpanded?: boolean | ExpandedState;
}

// Removed SubRowTable component - it was causing browser hangs due to multiple table instances

interface DataTableProps<TData extends ExportableData, TValue> {
  // Allow overriding the table configuration
  config?: Partial<TableConfig>;

  // Column definitions generator
  getColumns: (handleRowDeselection: ((rowId: string) => void) | null | undefined) => ColumnDef<TData, TValue>[];

  // Subrow column definitions generator (for custom-columns mode)
  getSubRowColumns?: (handleRowDeselection: ((rowId: string) => void) | null | undefined) => ColumnDef<any, unknown>[];

  // Data fetching function
  fetchDataFn: ((params: DataFetchParams) => Promise<DataFetchResult<TData>>) |
               ((page: number, pageSize: number, search: string, dateRange: { from_date: string; to_date: string }, sortBy: string, sortOrder: string, caseConfig?: CaseFormatConfig) => unknown);

  // Function to fetch specific items by their IDs
  fetchByIdsFn?: (ids: number[] | string[]) => Promise<TData[]>;

  // Export configuration
  exportConfig: {
    entityName: string;
    columnMapping: Record<string, string>;
    columnWidths: Array<{ wch: number }>;
    headers: string[];
    caseConfig?: CaseFormatConfig;
    transformFunction?: DataTransformFunction<TData>;
    // Enable/disable export formats (both enabled by default)
    enableCsv?: boolean;
    enableExcel?: boolean;
    // Subrow export configuration
    subRowExportConfig?: {
      entityName: string;
      columnMapping: Record<string, string>;
      columnWidths: Array<{ wch: number }>;
      headers: string[];
      transformFunction?: DataTransformFunction<TData>;
    };
  };

  // ID field in TData for tracking selected items
  idField: keyof TData;

  // Custom page size options
  pageSizeOptions?: number[];

  // Custom toolbar content render function
  renderToolbarContent?: (props: {
    selectedRows: TData[];
    allSelectedIds: string[];
    totalSelectedCount: number;
    resetSelection: () => void;
  }) => React.ReactNode;

  // Row click callback
  onRowClick?: (rowData: TData, rowIndex: number) => void;

  // Subrows configuration
  subRowsConfig?: SubRowsConfig<TData>;
}

export function DataTable<TData extends ExportableData, TValue>({
  config = {},
  getColumns,
  getSubRowColumns,
  fetchDataFn,
  fetchByIdsFn,
  exportConfig,
  idField = 'id' as keyof TData,
  pageSizeOptions,
  renderToolbarContent,
  onRowClick,
  subRowsConfig
}: DataTableProps<TData, TValue>) {
  // Load table configuration with any overrides
  const tableConfig = useTableConfig(config);

  // Table ID for localStorage storage - generate a default if not provided
  const tableId = tableConfig.columnResizingTableId || 'data-table-default';

  // Use our custom hook for column resizing
  const { columnSizing, setColumnSizing, resetColumnSizing } = useTableColumnResize(
    tableId,
    tableConfig.enableColumnResizing
  );

  // Create conditional URL state hook based on config
  const useConditionalUrlState = createConditionalStateHook(tableConfig.enableUrlState);

  // States for API parameters using conditional URL state
  const [page, setPage] = useConditionalUrlState("page", 1);
  const [pageSize, setPageSize] = useConditionalUrlState("pageSize", 10);
  const [search, setSearch] = useConditionalUrlState("search", "");
  const [dateRange, setDateRange] = useConditionalUrlState<{ from_date: string; to_date: string }>("dateRange", { from_date: "", to_date: "" });
  const [sortBy, setSortBy] = useConditionalUrlState("sortBy", tableConfig.defaultSortBy || "id");
  const [sortOrder, setSortOrder] = useConditionalUrlState<"asc" | "desc">("sortOrder", tableConfig.defaultSortOrder || "desc");
  const [columnVisibility, setColumnVisibility] = useConditionalUrlState<Record<string, boolean>>("columnVisibility", {});
  const [columnFilters, setColumnFilters] = useConditionalUrlState<Array<{ id: string; value: unknown }>>("columnFilters", []);

  // Internal states
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);
  const [error, setError] = useState<Error | null>(null);
  const [data, setData] = useState<{
    data: TData[];
    pagination: {
      page: number;
      limit: number;
      total_pages: number;
      total_items: number;
    };
  } | null>(null);

  // Column order state (managed separately from URL state as it's persisted in localStorage)
  const [columnOrder, setColumnOrder] = useState<string[]>([]);

  // Expanded state for subrows
  const [expanded, setExpanded] = useState<ExpandedState>(() => {
    const defaultVal = subRowsConfig?.defaultExpanded;
    if (typeof defaultVal === 'boolean') {
      return defaultVal ? true : {};
    }
    return defaultVal ?? {};
  });

  // Subrow sorting state - tracks sorting per parent row
  // Format: { [parentId]: { columnId: string, direction: 'asc' | 'desc' } }
  const [subrowSorting, setSubrowSorting] = useState<Record<string, { columnId: string; direction: 'asc' | 'desc' }>>({});

  // PERFORMANCE FIX: Use only one selection state as the source of truth
  // All IDs are stored as strings for consistency
  const [selectedItemIds, setSelectedItemIds] = useState<Record<string, boolean>>({});

  // Track parent and subrow IDs separately for accurate counts across pages
  const [selectedParentIds, setSelectedParentIds] = useState<Set<string>>(new Set());
  const [selectedSubrowIds, setSelectedSubrowIds] = useState<Set<string>>(new Set());
  // Track which parent each subrow belongs to (subrowId -> parentId)
  const [subrowToParentMap, setSubrowToParentMap] = useState<Map<string, string>>(new Map());

  // Convert the sorting from URL to the format TanStack Table expects
  const sorting = useMemo(() => createSortingState(sortBy, sortOrder), [sortBy, sortOrder]);

  // Function to sort subrows for a specific parent
  const getSortedSubrows = useCallback((parentId: string, subrows: any[]) => {
    const sortConfig = subrowSorting[parentId];

    if (!sortConfig || !subrows || subrows.length === 0) {
      return subrows;
    }

    const sorted = [...subrows].sort((a, b) => {
      const aValue = a[sortConfig.columnId];
      const bValue = b[sortConfig.columnId];

      // Handle null/undefined values
      if (aValue == null && bValue == null) return 0;
      if (aValue == null) return 1;
      if (bValue == null) return -1;

      // Compare values
      let comparison = 0;
      if (typeof aValue === 'string' && typeof bValue === 'string') {
        comparison = aValue.localeCompare(bValue);
      } else if (typeof aValue === 'number' && typeof bValue === 'number') {
        comparison = aValue - bValue;
      } else {
        comparison = String(aValue).localeCompare(String(bValue));
      }

      return sortConfig.direction === 'asc' ? comparison : -comparison;
    });

    return sorted;
  }, [subrowSorting]);

  // Get current data items - memoize to avoid recalculations
  // Apply subrow sorting if enabled
  const dataItems = useMemo(() => {
    const items = data?.data || [];

    // If subrows are not enabled or no sorting applied, return as-is
    if (!subRowsConfig?.enabled || Object.keys(subrowSorting).length === 0) {
      return items;
    }

    // Apply sorting to subrows for each parent
    return items.map(item => {
      const itemId = String(item[idField]);
      const subRows = (item as any)[subRowsConfig.subRowsField || 'subRows'];

      // If no subrows or no sorting for this parent, return as-is
      if (!Array.isArray(subRows) || !subrowSorting[itemId]) {
        return item;
      }

      // Sort the subrows
      const sortedSubrows = getSortedSubrows(itemId, subRows);

      // Return new item with sorted subrows
      return {
        ...item,
        [subRowsConfig.subRowsField || 'subRows']: sortedSubrows
      };
    });
  }, [data?.data, subRowsConfig, subrowSorting, idField, getSortedSubrows]);

  // PERFORMANCE FIX: rowSelection is now directly selectedItemIds (no conversion needed)
  // Since getRowId returns actual IDs, TanStack Table uses IDs as keys
  const rowSelection = selectedItemIds;

  // Calculate total selected items - memoize to avoid recalculation
  const totalSelectedItems = useMemo(() =>
    Object.keys(selectedItemIds).length,
    [selectedItemIds]
  );

  // Calculate parent and subrow counts across all pages using tracked Sets
  const totalParentCount = selectedParentIds.size;
  const totalSubrowCount = selectedSubrowIds.size;

  // PERFORMANCE FIX: Optimized row deselection handler
  const handleRowDeselection = useCallback((rowId: string) => {
    // With getRowId, rowId is already the actual item ID (not an index)
    setSelectedItemIds(prev => {
      const next = { ...prev };
      delete next[rowId];
      return next;
    });

    // Also remove from parent/subrow tracking
    if (subRowsConfig?.enabled) {
      setSelectedParentIds(prev => {
        const next = new Set(prev);
        next.delete(rowId);
        return next;
      });
      setSelectedSubrowIds(prev => {
        const next = new Set(prev);
        next.delete(rowId);
        return next;
      });
      setSubrowToParentMap(prev => {
        const next = new Map(prev);
        next.delete(rowId);
        return next;
      });
    }
  }, [subRowsConfig]);

  // Clear all selections
  const clearAllSelections = useCallback(() => {
    setSelectedItemIds({});
    setSelectedParentIds(new Set());
    setSelectedSubrowIds(new Set());
    setSubrowToParentMap(new Map());
  }, []);

  // PERFORMANCE FIX: Optimized row selection handler
  const handleRowSelectionChange = useCallback((updaterOrValue: RowSelectionUpdater | Record<string, boolean>) => {
    setSelectedItemIds(prev => {
      // Determine the new row selection value
      const newRowSelection = typeof updaterOrValue === 'function'
        ? updaterOrValue(prev)
        : updaterOrValue;

      // Update parent and subrow tracking sets if subrows are enabled
      if (subRowsConfig?.enabled) {
        // Build maps of parent and subrow IDs from current page data
        const parentIdsMap = new Map<string, boolean>();
        const subrowIdsMap = new Map<string, boolean>();
        const subrowParentMap = new Map<string, string>();

        dataItems.forEach((item) => {
          const itemId = String(item[idField]);
          parentIdsMap.set(itemId, true);

          const subRowsData = (item as any)[subRowsConfig.subRowsField || 'subRows'];
          if (Array.isArray(subRowsData)) {
            subRowsData.forEach((subRow: any) => {
              const subRowId = String(subRow[idField]);
              subrowIdsMap.set(subRowId, true);
              subrowParentMap.set(subRowId, itemId);
            });
          }
        });

        // Find newly selected and deselected IDs
        const prevIds = new Set(Object.keys(prev));
        const newIds = new Set(Object.keys(newRowSelection));

        const addedIds = Array.from(newIds).filter(id => !prevIds.has(id));
        const removedIds = Array.from(prevIds).filter(id => !newIds.has(id));

        // Update parent and subrow sets
        setSelectedParentIds(prevParents => {
          const newParents = new Set(prevParents);
          addedIds.forEach(id => {
            if (parentIdsMap.has(id)) newParents.add(id);
          });
          removedIds.forEach(id => {
            newParents.delete(id);
          });
          return newParents;
        });

        setSelectedSubrowIds(prevSubrows => {
          const newSubrows = new Set(prevSubrows);
          addedIds.forEach(id => {
            if (subrowIdsMap.has(id)) newSubrows.add(id);
          });
          removedIds.forEach(id => {
            newSubrows.delete(id);
          });
          return newSubrows;
        });

        // Update subrow-to-parent mapping
        setSubrowToParentMap(prevMap => {
          const newMap = new Map(prevMap);
          addedIds.forEach(id => {
            const parentId = subrowParentMap.get(id);
            if (parentId) newMap.set(id, parentId);
          });
          removedIds.forEach(id => {
            newMap.delete(id);
          });
          return newMap;
        });
      }

      // With getRowId, rowIds are actual item IDs (not indices)
      // So we can directly use newRowSelection as selectedItemIds
      return newRowSelection;
    });
  }, [dataItems, idField, subRowsConfig]);

  // Get selected items data
  const getSelectedItems = useCallback(async () => {
    // If nothing is selected, return empty array
    if (totalSelectedItems === 0) {
      return [];
    }

    // Get IDs of selected items - determine if they are strings or numbers
    const selectedIdsArray = Object.keys(selectedItemIds);

    // Check if the first item ID is a number to determine the type
    const isNumericIds = dataItems.length > 0 && typeof dataItems[0][idField] === 'number';

    // Find items from current page that are selected
    const itemsInCurrentPage = dataItems.filter(item =>
      selectedItemIds[String(item[idField])]
    );

    // Get IDs of items on current page
    const idsInCurrentPage = itemsInCurrentPage.map(item => String(item[idField]));

    // Find IDs that need to be fetched (not on current page)
    const idsToFetchRaw = selectedIdsArray.filter(id =>
      !idsInCurrentPage.includes(id)
    );

    // Convert IDs to the appropriate type for the fetchByIdsFn
    const idsToFetch = isNumericIds
      ? idsToFetchRaw.map(id => Number.parseInt(id, 10)).filter(id => !Number.isNaN(id))
      : idsToFetchRaw;

    // If all selected items are on current page or we can't fetch by IDs
    if (idsToFetch.length === 0 || !fetchByIdsFn) {
      return itemsInCurrentPage;
    }

    try {
      // Fetch missing items in a single batch - TypeScript will infer the correct type
      const fetchedItems = await fetchByIdsFn(idsToFetch as number[] | string[]);

      // Combine current page items with fetched items
      return [...itemsInCurrentPage, ...fetchedItems];
    } catch (error) {
      console.error("Error fetching selected items:", error);
      return itemsInCurrentPage;
    }
  }, [dataItems, selectedItemIds, totalSelectedItems, fetchByIdsFn, idField]);

  // Get all items on current page
  const getAllItems = useCallback((): TData[] => {
    // Return current page data
    return dataItems;
  }, [dataItems]);

  // SUBROW HELPERS: Get selected parents and subrows separately
  const getSelectedParentsAndSubrows = useCallback(() => {
    if (!subRowsConfig?.enabled) {
      return { parents: [], subrows: [], parentIds: [], subrowIds: [] };
    }

    const parents: TData[] = [];
    const subrows: Array<{ parentId: string | number; subrow: TData }> = [];
    const parentIds: Array<string | number> = [];
    const subrowIds: Array<string | number> = [];

    dataItems.forEach((item) => {
      const itemId = String(item[idField]);
      const isParentSelected = selectedItemIds[itemId];

      if (isParentSelected) {
        parents.push(item);
        const id = item[idField];
        if (id !== null && id !== undefined) {
          parentIds.push(id as string | number);
        }
      }

      // Check subrows
      const subRowsData = (item as any)[subRowsConfig.subRowsField || 'subRows'];
      if (Array.isArray(subRowsData)) {
        subRowsData.forEach((subRow: any) => {
          const subRowId = String(subRow[idField]);
          if (selectedItemIds[subRowId]) {
            const parentId = item[idField];
            const subRowItemId = subRow[idField];
            subrows.push({
              parentId: (parentId !== null && parentId !== undefined) ? parentId as string | number : 0,
              subrow: subRow as TData
            });
            if (subRowItemId !== null && subRowItemId !== undefined) {
              subrowIds.push(subRowItemId as string | number);
            }
          }
        });
      }
    });

    return { parents, subrows, parentIds, subrowIds };
  }, [dataItems, selectedItemIds, idField, subRowsConfig]);

  // Get only selected parent rows (for parent export) - with cross-page support
  const getSelectedParentRows = useCallback(async (): Promise<TData[]> => {
    if (!subRowsConfig?.enabled) {
      return [];
    }

    // Get all selected parent IDs
    const allParentIds = Array.from(selectedParentIds);

    if (allParentIds.length === 0) {
      return [];
    }

    // Check if the first item ID is a number to determine the type
    const isNumericIds = dataItems.length > 0 && typeof dataItems[0][idField] === 'number';

    // Find parent items from current page
    const parentsInCurrentPage = dataItems.filter(item =>
      selectedParentIds.has(String(item[idField]))
    );

    // Get IDs of parents on current page
    const idsInCurrentPage = new Set(parentsInCurrentPage.map(item => String(item[idField])));

    // Find parent IDs that need to be fetched (not on current page)
    const idsToFetchRaw = allParentIds.filter(id => !idsInCurrentPage.has(id));

    // If all selected parents are on current page or we can't fetch by IDs
    if (idsToFetchRaw.length === 0 || !fetchByIdsFn) {
      return parentsInCurrentPage;
    }

    try {
      // Convert IDs to the appropriate type for the fetchByIdsFn
      const idsToFetch = isNumericIds
        ? idsToFetchRaw.map(id => Number.parseInt(id, 10)).filter(id => !Number.isNaN(id))
        : idsToFetchRaw;

      // Fetch missing parent items in a single batch
      const fetchedItems = await fetchByIdsFn(idsToFetch as number[] | string[]);

      // Filter fetched items to only include parents (not subrows)
      const fetchedParents = fetchedItems.filter((item: any) => {
        // A parent row either has no item_id, or has parent-level fields
        // We can identify parents by checking if they have subRows or parent-level fields
        return !item.item_id || item.total_amount !== undefined || item.status !== undefined;
      });

      // Combine current page parents with fetched parents
      return [...parentsInCurrentPage, ...fetchedParents];
    } catch (error) {
      console.error("Error fetching selected parent rows:", error);
      return parentsInCurrentPage;
    }
  }, [selectedParentIds, dataItems, fetchByIdsFn, idField, subRowsConfig]);

  // Get only selected subrows (for subrow export) - with cross-page support
  const getSelectedSubRows = useCallback(async (): Promise<TData[]> => {
    if (!subRowsConfig?.enabled) {
      return [];
    }

    // Get all selected subrow IDs
    const allSubrowIds = Array.from(selectedSubrowIds);

    if (allSubrowIds.length === 0) {
      return [];
    }

    // Check if the first item ID is a number to determine the type
    const isNumericIds = dataItems.length > 0 && typeof dataItems[0][idField] === 'number';

    // Find subrow items from current page
    const subrowsInCurrentPage: TData[] = [];
    dataItems.forEach((item) => {
      const subRowsData = (item as any)[subRowsConfig.subRowsField || 'subRows'];
      if (Array.isArray(subRowsData)) {
        subRowsData.forEach((subRow: any) => {
          if (selectedSubrowIds.has(String(subRow[idField]))) {
            subrowsInCurrentPage.push(subRow as TData);
          }
        });
      }
    });

    // Get IDs of subrows on current page
    const idsInCurrentPage = new Set(subrowsInCurrentPage.map(item => String(item[idField])));

    // Find subrow IDs that need to be fetched (not on current page)
    const subrowIdsToFetch = allSubrowIds.filter(id => !idsInCurrentPage.has(id));

    // If all selected subrows are on current page or we can't fetch by IDs
    if (subrowIdsToFetch.length === 0 || !fetchByIdsFn) {
      return subrowsInCurrentPage;
    }

    try {
      // Find the parent order IDs for subrows that need to be fetched
      const parentIdsToFetch = new Set<string>();
      subrowIdsToFetch.forEach(subrowId => {
        const parentId = subrowToParentMap.get(subrowId);
        if (parentId) {
          parentIdsToFetch.add(parentId);
        }
      });

      if (parentIdsToFetch.size === 0) {
        console.warn("No parent IDs found for selected subrows. This may indicate a selection tracking issue.");
        return subrowsInCurrentPage;
      }

      // Convert parent IDs to the appropriate type for the fetchByIdsFn
      const parentIdsArray = Array.from(parentIdsToFetch);
      const idsToFetch = isNumericIds
        ? parentIdsArray.map(id => Number.parseInt(id, 10)).filter(id => !Number.isNaN(id))
        : parentIdsArray;

      // Fetch parent orders that contain the selected subrows
      const fetchedParentOrders = await fetchByIdsFn(idsToFetch as number[] | string[]);

      // Extract only the selected subrows from fetched parent orders
      const fetchedSubrows: TData[] = [];
      fetchedParentOrders.forEach((item: any) => {
        const subRowsData = (item as any)[subRowsConfig.subRowsField || 'subRows'];
        if (Array.isArray(subRowsData)) {
          subRowsData.forEach((subRow: any) => {
            if (selectedSubrowIds.has(String(subRow[idField]))) {
              fetchedSubrows.push(subRow as TData);
            }
          });
        }
      });

      // Combine current page subrows with fetched subrows
      return [...subrowsInCurrentPage, ...fetchedSubrows];
    } catch (error) {
      console.error("Error fetching selected subrows:", error);
      return subrowsInCurrentPage;
    }
  }, [selectedSubrowIds, subrowToParentMap, dataItems, fetchByIdsFn, idField, subRowsConfig]);

  // Fetch data
  useEffect(() => {
    // Check if the fetchDataFn is a query hook
    const isQueryHook = (fetchDataFn as { isQueryHook?: boolean }).isQueryHook === true;

    if (!isQueryHook) {
      // Create refs to capture the current sort values at the time of fetching
      const currentSortBy = sortBy;
      const currentSortOrder = sortOrder;

      const fetchData = async () => {
        try {
          setIsLoading(true);

          const result = await (fetchDataFn as (params: DataFetchParams) => Promise<DataFetchResult<TData>>)({
            page,
            limit: pageSize,
            search: preprocessSearch(search),
            from_date: dateRange.from_date,
            to_date: dateRange.to_date,
            sort_by: currentSortBy,
            sort_order: currentSortOrder,
          });
          setData(result);
          setIsError(false);
          setError(null);
        } catch (err) {
          setIsError(true);
          setError(err instanceof Error ? err : new Error("Unknown error"));
          console.error("Error fetching data:", err);
        } finally {
          setIsLoading(false);
        }
      };

      fetchData();
    }
  }, [page, pageSize, search, dateRange, sortBy, sortOrder, fetchDataFn]);

  // If fetchDataFn is a React Query hook, call it directly with parameters
  const queryResult = (fetchDataFn as { isQueryHook?: boolean }).isQueryHook === true
    ? (fetchDataFn as (page: number, pageSize: number, search: string, dateRange: { from_date: string; to_date: string }, sortBy: string, sortOrder: string, caseConfig?: CaseFormatConfig) => { 
        isLoading: boolean; 
        isSuccess: boolean; 
        isError: boolean; 
        data?: DataFetchResult<TData>; 
        error?: Error 
      })(
        page, 
        pageSize, 
        search, 
        dateRange, 
        sortBy,
        sortOrder,
        exportConfig.caseConfig
      )
    : null;

  // If using React Query, update state based on query result
  useEffect(() => {
    if (queryResult) {
      setIsLoading(queryResult.isLoading);
      if (queryResult.isSuccess && queryResult.data) {
        setData(queryResult.data);
        setIsError(false);
        setError(null);
      }
      if (queryResult.isError) {
        setIsError(true);
        setError(queryResult.error instanceof Error ? queryResult.error : new Error("Unknown error"));
      }
    }
  }, [
    queryResult?.data,
    queryResult?.isLoading,
    queryResult?.isSuccess,
    queryResult?.isError,
    queryResult?.error,
  ]);

  // Memoized pagination state
  const pagination = useMemo(
    () => ({
      pageIndex: page - 1,
      pageSize,
    }),
    [page, pageSize]
  );

  // Ref for the table container for keyboard navigation
  const tableContainerRef = useRef<HTMLDivElement>(null);

  // Get columns with the deselection handler (memoize to avoid recreation on render)
  const columns = useMemo(() => {
    // Only pass deselection handler if row selection is enabled
    return getColumns(tableConfig.enableRowSelection ? handleRowDeselection : null);
  }, [getColumns, handleRowDeselection, tableConfig.enableRowSelection]);

  // Get subrow columns if using custom-columns mode
  const subRowColumns = useMemo(() => {
    if (subRowsConfig?.enabled && subRowsConfig.mode === 'custom-columns' && getSubRowColumns) {
      return getSubRowColumns(tableConfig.enableRowSelection ? handleRowDeselection : null);
    }
    return null;
  }, [getSubRowColumns, handleRowDeselection, tableConfig.enableRowSelection, subRowsConfig]);

  // Removed subrow table states - was causing performance issues

  // Create event handlers using utility functions
  const handleSortingChange = useCallback(
    (updaterOrValue: SortingUpdater | { id: string; desc: boolean }[]) => {
      // Extract the new sorting state
      const newSorting = typeof updaterOrValue === 'function'
        ? updaterOrValue(sorting)
        : updaterOrValue;

      if (newSorting.length > 0) {
        const columnId = newSorting[0].id;
        const direction = newSorting[0].desc ? "desc" : "asc";
        // Use Promise.all for batch updates to ensure they're applied together
        Promise.all([
          setSortBy(columnId),
          setSortOrder(direction)
        ]).catch(err => {
          console.error("Failed to update URL sorting params:", err);
        });
      }
    },
    [setSortBy, setSortOrder, sorting]
  );

  const handleColumnFiltersChange = useCallback(
    createColumnFiltersHandler(setColumnFilters),
    []
  );

  const handleColumnVisibilityChange = useCallback(
    createColumnVisibilityHandler(setColumnVisibility),
    []
  );

  const handlePaginationChange = useCallback(
    (updaterOrValue: PaginationUpdater<TData> | { pageIndex: number; pageSize: number }) => {
      // Extract the new pagination state
      const newPagination = typeof updaterOrValue === 'function'
        ? updaterOrValue({ pageIndex: page - 1, pageSize })
        : updaterOrValue;

      // Special handling: When page size changes, always reset to page 1
      if (newPagination.pageSize !== pageSize) {
        // First, directly update URL to ensure it's in sync
        const url = new URL(window.location.href);
        url.searchParams.set('pageSize', String(newPagination.pageSize));
        url.searchParams.set('page', '1'); // Always reset to page 1
        window.history.replaceState({}, '', url.toString());

        // Then update our state
        setPageSize(newPagination.pageSize);
        setPage(1);
        return;
      }

      // Only update page if it's changed - this handles normal page navigation
      if ((newPagination.pageIndex + 1) !== page) {
        const setPagePromise = setPage(newPagination.pageIndex + 1);
        if (setPagePromise && typeof setPagePromise.catch === 'function') {
          setPagePromise.catch(err => {
            console.error("Failed to update page param:", err);
          });
        }
      }
    },
    [page, pageSize, setPage, setPageSize]
  );

  const handleColumnSizingChange = useCallback(
    (updaterOrValue: ColumnSizingState | ((prev: ColumnSizingState) => ColumnSizingState)) => {
      if (typeof updaterOrValue === 'function') {
        setColumnSizing(current => updaterOrValue(current));
      } else {
        setColumnSizing(updaterOrValue);
      }
    },
    [setColumnSizing]
  );

  // Column order change handler
  const handleColumnOrderChange = useCallback((updaterOrValue: ColumnOrderUpdater | string[]) => {
    const newColumnOrder = typeof updaterOrValue === 'function'
      ? updaterOrValue(columnOrder)
      : updaterOrValue;

    setColumnOrder(newColumnOrder);

    // Persist column order to localStorage
    try {
      localStorage.setItem('data-table-column-order', JSON.stringify(newColumnOrder));
    } catch (error) {
      console.error('Failed to save column order to localStorage:', error);
    }
  }, [columnOrder]);

  // Load column order from localStorage on initial render
  useEffect(() => {
    try {
      const savedOrder = localStorage.getItem('data-table-column-order');
      if (savedOrder) {
        const parsedOrder = JSON.parse(savedOrder);
        // Validate array of strings
        if (Array.isArray(parsedOrder) && parsedOrder.every(item => typeof item === 'string')) {
          setColumnOrder(parsedOrder);
        } else {
          console.warn('Invalid column order format, ignoring');
          localStorage.removeItem('data-table-column-order');
        }
      }
    } catch (error) {
      console.error('Error loading column order:', error);
      localStorage.removeItem('data-table-column-order');
    }
  }, []);

  // Memoize table configuration to prevent unnecessary re-renders
  const tableOptions = useMemo(() => ({
    data: dataItems,
    columns,
    state: {
      sorting,
      columnVisibility,
      rowSelection,
      columnFilters,
      pagination,
      columnSizing,
      columnOrder,
      ...(subRowsConfig?.enabled && { expanded }),
    },
    columnResizeMode: 'onChange' as ColumnResizeMode,
    onColumnSizingChange: handleColumnSizingChange,
    onColumnOrderChange: handleColumnOrderChange,
    pageCount: data?.pagination.total_pages || 0,
    enableRowSelection: tableConfig.enableRowSelection,
    enableColumnResizing: tableConfig.enableColumnResizing,
    // SUBROW SELECTION: Disable automatic cascading
    // We handle parent-child selection manually in the checkbox handler
    enableSubRowSelection: false,
    // SUBROW ID FIX: Generate unique composite IDs to avoid collisions
    // Without this, parent ID=1 and subrow ID=1 would both be "1"
    getRowId: (row: TData, index: number, parent?: Row<TData>) => {
      const rowId = String((row as any)[idField]);
      if (subRowsConfig?.enabled && parent) {
        // Subrow: create composite ID like "438-sub-3706"
        return `${parent.id}-sub-${rowId}`;
      }
      // Parent: use direct ID like "438"
      return rowId;
    },
    manualPagination: true,
    manualSorting: true,
    manualFiltering: true,
    onRowSelectionChange: handleRowSelectionChange,
    onSortingChange: handleSortingChange,
    onColumnFiltersChange: handleColumnFiltersChange,
    onColumnVisibilityChange: handleColumnVisibilityChange,
    onPaginationChange: handlePaginationChange,
    ...(subRowsConfig?.enabled && {
      onExpandedChange: setExpanded,
      getExpandedRowModel: getExpandedRowModel(),
      getSubRows: (row: TData) => {
        const subRows = (row as any)[subRowsConfig.subRowsField || 'subRows'];
        // Only return if it's a valid array with items
        return Array.isArray(subRows) && subRows.length > 0 ? subRows : undefined;
      },
    }),
    getCoreRowModel: getCoreRowModel<TData>(),
    getFilteredRowModel: getFilteredRowModel<TData>(),
    getPaginationRowModel: getPaginationRowModel<TData>(),
    getSortedRowModel: getSortedRowModel<TData>(),
    getFacetedRowModel: getFacetedRowModel<TData>(),
    getFacetedUniqueValues: getFacetedUniqueValues<TData>(),
  }), [
    dataItems,
    columns,
    sorting,
    columnVisibility,
    rowSelection,
    columnFilters,
    pagination,
    columnSizing,
    columnOrder,
    expanded,
    handleColumnSizingChange,
    handleColumnOrderChange,
    data?.pagination.total_pages,
    tableConfig.enableRowSelection,
    tableConfig.enableColumnResizing,
    handleRowSelectionChange,
    handleSortingChange,
    handleColumnFiltersChange,
    handleColumnVisibilityChange,
    handlePaginationChange,
    subRowsConfig,
    idField,
  ]);

  // Set up the table with memoized configuration
  const table = useReactTable<TData>(tableOptions);

  // Row click handler with conflict prevention
  const handleRowClick = useCallback((event: React.MouseEvent, rowData: TData, rowIndex: number) => {
    // Prevent row click if clicking on interactive elements (buttons, links, etc.)
    const target = event.target as HTMLElement;
    const isInteractiveElement = target.closest('button, a, input, select, textarea, [role="button"], [role="link"]');

    if (isInteractiveElement) {
      return;
    }

    // Call the onRowClick callback if provided
    onRowClick?.(rowData, rowIndex);
  }, [onRowClick]);

  // Create keyboard navigation handler
  const handleKeyDown = useCallback(
    createKeyboardNavigationHandler(table, onRowClick ? (rowData: TData, rowIndex: number) => {
      // Handle keyboard activation (Enter/Space) for row clicks
      onRowClick(rowData, rowIndex);
    } : undefined),
    [onRowClick]
  );

  // Add an effect to validate page number when page size changes
  useEffect(() => {
    // This effect ensures page is valid after page size changes
    const totalPages = data?.pagination.total_pages || 0;

    if (totalPages > 0 && page > totalPages) {
      setPage(1);
    }
  }, [data?.pagination?.total_pages, page, setPage]);

  // Reset to page 1 when column filters change (not on every render)
  const prevColumnFiltersRef = useRef<typeof columnFilters>(columnFilters);
  useEffect(() => {
    // Check if columnFilters actually changed (not just page navigation)
    const filtersChanged = JSON.stringify(prevColumnFiltersRef.current) !== JSON.stringify(columnFilters);
    if (filtersChanged && page !== 1) {
      setPage(1);
    }
    prevColumnFiltersRef.current = columnFilters;
  }, [columnFilters, page, setPage]);

  // Initialize default column sizes when columns are available and no saved sizes exist
  useEffect(() => {
    initializeColumnSizes(columns as ColumnDef<TData, unknown>[], tableId, setColumnSizing);
  }, [columns, tableId, setColumnSizing]);

  // Handle column resizing
  useEffect(() => {
    const isResizingAny =
      table.getHeaderGroups().some(headerGroup =>
        headerGroup.headers.some(header => header.column.getIsResizing())
      );

    trackColumnResizing(isResizingAny);

    // Cleanup on unmount
    return () => {
      cleanupColumnResizing();
    };
  }, [table]);

  // Reset column order
  const resetColumnOrder = useCallback(() => {
    // Reset to empty array (which resets to default order)
    table.setColumnOrder([]);
    setColumnOrder([]);

    // Remove from localStorage
    try {
      localStorage.removeItem('data-table-column-order');
    } catch (error) {
      console.error('Failed to remove column order from localStorage:', error);
    }
  }, [table]);

  // Add synchronization effect to ensure URL is the source of truth
  useEffect(() => {
    // Force the table's sorting state to match URL parameters
    table.setSorting(sorting);
  }, [table, sorting]);

  // Subrow sorting handler
  const handleSubrowHeaderClick = useCallback((parentId: string, columnId: string) => {
    setSubrowSorting(prev => {
      const currentSort = prev[parentId];

      // If clicking the same column, toggle direction
      if (currentSort?.columnId === columnId) {
        return {
          ...prev,
          [parentId]: {
            columnId,
            direction: currentSort.direction === 'asc' ? 'desc' : 'asc'
          }
        };
      }

      // Otherwise, set new column with ascending order
      return {
        ...prev,
        [parentId]: {
          columnId,
          direction: 'asc'
        }
      };
    });
  }, []);

  // Keep pagination in sync with URL parameters
  useEffect(() => {
    // Make sure table pagination state matches URL state
    const tableState = table.getState().pagination;
    if (tableState.pageIndex !== page - 1 || tableState.pageSize !== pageSize) {
      // Avoid unnecessary updates that might cause infinite loops
      if (tableState.pageSize !== pageSize || Math.abs(tableState.pageIndex - (page - 1)) > 0) {
        table.setPagination({
          pageIndex: page - 1,
          pageSize: pageSize
        });
      }
    }
  }, [table, page, pageSize]);

  // Handle error state
  if (isError) {
    return (
      <Alert variant="destructive" className="my-4">
        <AlertCircle className="h-4 w-4" />
        <AlertTitle>Error</AlertTitle>
        <AlertDescription>
          Failed to load data: {error instanceof Error ? error.message : "Unknown error"}
        </AlertDescription>
      </Alert>
    );
  }

  return (
    <div className="space-y-4">
      {tableConfig.enableToolbar && (
        <DataTableToolbar
          table={table}
          search={search}
          dateRange={dateRange}
          setSearch={setSearch}
          setDateRange={setDateRange}
          totalSelectedItems={totalSelectedItems}
          deleteSelection={clearAllSelections}
          getSelectedItems={getSelectedItems}
          getAllItems={getAllItems}
          config={tableConfig}
          resetColumnSizing={() => {
            resetColumnSizing();
            // Force a small delay and then refresh the UI
            setTimeout(() => {
              window.dispatchEvent(new Event('resize'));
            }, 100);
          }}
          resetColumnOrder={resetColumnOrder}
          entityName={exportConfig.entityName}
          columnMapping={exportConfig.columnMapping}
          columnWidths={exportConfig.columnWidths}
          headers={exportConfig.headers}
          transformFunction={exportConfig.transformFunction}
          // Subrow-specific props
          subRowsConfig={subRowsConfig}
          getSelectedParentsAndSubrows={getSelectedParentsAndSubrows}
          getSelectedParentRows={getSelectedParentRows}
          getSelectedSubRows={getSelectedSubRows}
          totalParentCount={totalParentCount}
          totalSubrowCount={totalSubrowCount}
          enableCsv={exportConfig.enableCsv !== false}
          enableExcel={exportConfig.enableExcel !== false}
          subRowExportConfig={exportConfig.subRowExportConfig}
          customToolbarComponent={renderToolbarContent?.({
            selectedRows: dataItems.filter((item) => selectedItemIds[String(item[idField])]),
            allSelectedIds: Object.keys(selectedItemIds),
            totalSelectedCount: totalSelectedItems,
            resetSelection: clearAllSelections
          })}
        />
      )}

      <div
        ref={tableContainerRef}
        className="overflow-y-auto rounded-md border table-container"
        aria-label="Data table"
        onKeyDown={tableConfig.enableKeyboardNavigation ? handleKeyDown : undefined}
      >
        <Table className={tableConfig.enableColumnResizing ? "resizable-table" : ""}>
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow
                key={headerGroup.id}
              >
                {headerGroup.headers.map((header) => (
                  <TableHead
                    className="px-2 py-2 relative text-left group/th"
                    key={header.id}
                    colSpan={header.colSpan}
                    scope="col"
                    tabIndex={-1}
                    style={{
                      width: header.getSize(),
                    }}
                    data-column-resizing={header.column.getIsResizing() ? "true" : undefined}
                  >
                    {header.isPlaceholder
                      ? null
                      : flexRender(
                          header.column.columnDef.header,
                          header.getContext(),
                        )}
                    {tableConfig.enableColumnResizing && header.column.getCanResize() && (
                      <DataTableResizer header={header} table={table} />
                    )}
                  </TableHead>
                ))}
              </TableRow>
            ))}
          </TableHeader>

          <TableBody>
            {isLoading ? (
              // Loading state
              Array.from({ length: pageSize }).map((_, i) => (
                <TableRow
                  key={`loading-row-${crypto.randomUUID()}`}
                  tabIndex={-1}
                >
                  {Array.from({ length: columns.length }).map((_, j, array) => (
                    <TableCell
                      key={`skeleton-cell-${crypto.randomUUID()}`}
                      className="px-4 py-2 truncate max-w-0 text-left"
                      tabIndex={-1}
                    >
                      <Skeleton className="h-6 w-full" />
                    </TableCell>
                  ))}
                </TableRow>
              ))
            ) : table.getRowModel().rows?.length ? (
              // Data rows
              table.getRowModel().rows.map((row, rowIndex) => {
                const isSubRow = row.depth > 0;
                const indentSize = subRowsConfig?.indentSize || 24;

                // For custom-component mode with subrows
                if (subRowsConfig?.enabled && subRowsConfig.mode === 'custom-component' && isSubRow) {
                  const SubRowComponent = subRowsConfig.SubRowComponent;
                  if (SubRowComponent) {
                    return (
                      <TableRow
                        key={row.id}
                        id={`row-${rowIndex}`}
                        data-row-index={rowIndex}
                        data-depth={row.depth}
                        className=""
                      >
                        <TableCell colSpan={columns.length} className="p-0">
                          <SubRowComponent row={row} data={row.original} />
                        </TableCell>
                      </TableRow>
                    );
                  }
                }

                // For custom-columns mode with subrows - render directly without SubRowTable
                if (subRowsConfig?.enabled && subRowsConfig.mode === 'custom-columns' && isSubRow && subRowColumns) {
                  const parentRow = row.getParentRow();
                  if (!parentRow) return null;

                  // Check if this is the first subrow of this parent
                  // We do this by checking the row index in the flat rows array
                  const flatRows = table.getRowModel().flatRows;
                  const currentRowIndex = flatRows.findIndex(r => r.id === row.id);
                  const prevRowIndex = currentRowIndex - 1;
                  const isFirstSubRow = prevRowIndex >= 0 && flatRows[prevRowIndex].id === parentRow.id;

                  // Render header row separately if this is the first subrow
                  if (isFirstSubRow && subRowsConfig.showSubRowHeaders) {
                    const parentId = String(parentRow.original[idField]);
                    const currentSort = subrowSorting[parentId];

                    return (
                      <React.Fragment key={`subrow-group-${row.id}`}>
                        <TableRow
                          key={`subrow-header-${parentRow.id}`}
                          data-subrow-header="true"
                          className="border-t"
                        >
                          {subRowColumns.map((column, colIndex) => {
                            // Get header text - handle both string and function headers
                            let headerText = '';
                            if (typeof column.header === 'string') {
                              headerText = column.header;
                            } else if (column.id === 'expand') {
                              headerText = ''; // Empty for expand column
                            } else if (column.id === 'select') {
                              headerText = ''; // Empty for select column
                            } else if (column.id === 'actions') {
                              headerText = 'Actions';
                            } else if ((column as any).meta?.title) {
                              headerText = (column as any).meta.title;
                            }

                            // Check if this column is sortable (has accessorKey)
                            const accessorKey = (column as any).accessorKey;
                            const isSortable = !!accessorKey && column.id !== 'select' && column.id !== 'actions' && column.id !== 'expand';
                            const columnId = accessorKey || column.id || '';
                            const isSorted = currentSort?.columnId === columnId;
                            const sortDirection = isSorted ? currentSort.direction : null;

                            return (
                              <TableCell
                                key={`subheader-${parentRow.id}-${colIndex}`}
                                className={cn(
                                  "px-2 py-2 text-left relative bg-muted/20 font-medium text-sm h-10 align-middle",
                                  isSortable && "cursor-pointer hover:bg-muted transition-colors select-none"
                                )}
                                style={{
                                  width: column.size || 'auto',
                                }}
                                onClick={isSortable ? () => handleSubrowHeaderClick(parentId, columnId) : undefined}
                              >
                                <div className="flex items-center gap-1.5">
                                  <span>{headerText}</span>
                                  {isSortable && (
                                    <span className="text-xs opacity-50 hover:opacity-100 transition-opacity">
                                      {isSorted ? (
                                        sortDirection === 'asc' ? (
                                          <span className="inline-block"></span>
                                        ) : (
                                          <span className="inline-block"></span>
                                        )
                                      ) : (
                                        <span className="inline-block opacity-40"></span>
                                      )}
                                    </span>
                                  )}
                                </div>
                              </TableCell>
                            );
                          })}
                        </TableRow>

                        {/* Render current subrow with SUBROW COLUMNS */}
                        <TableRow
                          key={row.id}
                          data-state={row.getIsSelected() ? "selected" : undefined}
                          tabIndex={0}
                          aria-selected={row.getIsSelected()}
                          className=""
                        >
                          {subRowColumns.map((column, colIndex) => {
                            // Get the value from the subrow data
                            const accessorKey = (column as any).accessorKey;
                            const value = accessorKey 
                              ? (row.original as any)[accessorKey]
                              : null;

                            return (
                              <TableCell
                                key={`subrow-cell-${row.id}-${colIndex}`}
                                className="px-4 py-2 truncate max-w-0 text-left"
                                style={{
                                  width: column.size || 'auto',
                                }}
                              >
                                {/* Render cell content */}
                                {column.cell && typeof column.cell === 'function'
                                  ? column.cell({ 
                                      getValue: () => value,
                                      row,
                                      column,
                                      cell: { getValue: () => value } as any,
                                      table
                                    } as any)
                                  : value}
                              </TableCell>
                            );
                          })}
                        </TableRow>
                      </React.Fragment>
                    );
                  }

                  // For non-first subrows, just render the row
                  return (
                    <TableRow
                      key={row.id}
                      data-state={row.getIsSelected() ? "selected" : undefined}
                      tabIndex={0}
                      aria-selected={row.getIsSelected()}
                      className=""
                    >
                      {subRowColumns.map((column, colIndex) => {
                        // Get the value from the subrow data
                        const accessorKey = (column as any).accessorKey;
                        const value = accessorKey 
                          ? (row.original as any)[accessorKey]
                          : null;

                        return (
                          <TableCell
                            key={`subrow-cell-${row.id}-${colIndex}`}
                            className="px-4 py-2 truncate max-w-0 text-left"
                            style={{
                              width: column.size || 'auto',
                            }}
                          >
                            {/* Render cell content */}
                            {column.cell && typeof column.cell === 'function'
                              ? column.cell({ 
                                  getValue: () => value,
                                  row,
                                  column,
                                  cell: { getValue: () => value } as any,
                                  table
                                } as any)
                              : value}
                          </TableCell>
                        );
                      })}
                    </TableRow>
                  );
                }

                // Default rendering (same-columns mode or parent rows)
                return (
                  <TableRow
                    key={row.id}
                    id={`row-${rowIndex}`}
                    data-row-index={rowIndex}
                    data-depth={row.depth}
                    data-state={row.getIsSelected() ? "selected" : undefined}
                    tabIndex={0}
                    aria-selected={row.getIsSelected()}
                    className=""
                    onClick={(event) => {
                      if (tableConfig.enableClickRowSelect) {
                        row.toggleSelected();
                      }
                      if (onRowClick) {
                        handleRowClick(event, row.original, rowIndex);
                      }
                    }}
                    onFocus={(e) => {
                      for (const el of document.querySelectorAll('[data-focused="true"]')) {
                        el.removeAttribute('data-focused');
                      }
                      e.currentTarget.setAttribute('data-focused', 'true');
                    }}
                    style={{
                      cursor: onRowClick ? 'pointer' : undefined
                    }}
                  >
                    {row.getVisibleCells().map((cell, cellIndex) => (
                      <TableCell
                        className="px-4 py-2 truncate max-w-0 text-left"
                        key={cell.id}
                        id={`cell-${rowIndex}-${cellIndex}`}
                        data-cell-index={cellIndex}
                        style={{
                          paddingLeft: isSubRow && cellIndex === 0 ? `${indentSize + 16}px` : undefined
                        }}
                      >
                        {flexRender(
                          cell.column.columnDef.cell,
                          cell.getContext(),
                        )}
                      </TableCell>
                    ))}
                  </TableRow>
                );
              })
            ) : (
              // No results
              <TableRow>
                <TableCell
                  colSpan={columns.length}
                  className="h-24 text-left truncate"
                >
                  No results.
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>

      {tableConfig.enablePagination && (
        <DataTablePagination
          table={table}
          totalItems={data?.pagination.total_items || 0}
          totalSelectedItems={totalSelectedItems}
          pageSizeOptions={pageSizeOptions || [10, 20, 30, 40, 50]}
          size={tableConfig.size}
        />
      )}
    </div>
  );
}

import {
  ArrowDownIcon,
  ArrowUpIcon,
  CaretSortIcon,
  EyeNoneIcon,
} from "@radix-ui/react-icons";
import type { Column } from "@tanstack/react-table";

import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger
} from "@/components/ui/dropdown-menu";


interface DataTableColumnHeaderProps<TData, TValue>
  extends React.HTMLAttributes<HTMLDivElement> {
  column: Column<TData, TValue>;
  title: string;
}

export function DataTableColumnHeader<TData, TValue>({
  column,
  title,
  className
}: DataTableColumnHeaderProps<TData, TValue>) {
  if (!column.getCanSort()) {
    return <div className={cn(className)}>{title}</div>;
  }

  // Get the current sort direction for this column
  const currentDirection = column.getIsSorted();

  // Use direct method to set sort with an explicit direction
  const setSorting = (direction: "asc" | "desc" | false) => {
    // If we're clearing sort, use an empty array
    if (direction === false) {
      column.toggleSorting(undefined, false);
      return;
    }

    // Set explicit sort with the direction
    // The second param (false) prevents multi-sort
    column.toggleSorting(direction === "desc", false);
  };

  return (
    <div className={cn("flex items-center space-x-2", className)}>
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button
            variant="ghost"
            size="sm"
            className="data-[state=open]:bg-accent h-8 focus-visible:ring-0 focus-visible:ring-offset-0"
          >
            <span>{title}</span>
            {currentDirection === "desc" ? (
              <ArrowDownIcon className="ml-2 h-4 w-4" />
            ) : currentDirection === "asc" ? (
              <ArrowUpIcon className="ml-2 h-4 w-4" />
            ) : (
              <CaretSortIcon className="ml-2 h-4 w-4" />
            )}
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent align="start">
          <DropdownMenuItem onClick={() => setSorting("asc")}>
            <ArrowUpIcon className="mr-2 h-3.5 w-3.5 text-muted-foreground/70" />
            Asc
          </DropdownMenuItem>
          <DropdownMenuItem onClick={() => setSorting("desc")}>
            <ArrowDownIcon className="mr-2 h-3.5 w-3.5 text-muted-foreground/70" />
            Desc
          </DropdownMenuItem>
          <DropdownMenuSeparator />
          <DropdownMenuItem  onClick={() => column.toggleVisibility(false)}>
            <EyeNoneIcon className="mr-2 h-3.5 w-3.5 text-muted-foreground/70" />
            Hide
          </DropdownMenuItem>
        </DropdownMenuContent>
      </DropdownMenu>
    </div>
  );
}

"use client";

// ** import types
import type { JSX } from "react";
import type { Table } from "@tanstack/react-table";
import type { ExportableData, DataTransformFunction } from "./utils/export-utils";
import type { TableConfig } from "./utils/table-config";

// ** import core packages
import { useState } from "react";
import { DownloadIcon, Loader2 } from "lucide-react";
import { toast } from "sonner";

// ** import components
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

// ** import utils
import { exportData, exportToCSV, exportToExcel } from "./utils/export-utils";

interface DataTableExportProps<TData extends ExportableData> {
  table: Table<TData>;
  data: TData[];
  selectedData?: TData[];
  getSelectedItems?: () => Promise<TData[]>;
  getAllItems?: () => Promise<TData[]>;
  entityName?: string;
  columnMapping?: Record<string, string>;
  columnWidths?: Array<{ wch: number }>;
  headers?: string[];
  transformFunction?: DataTransformFunction<TData>;
  size?: 'sm' | 'default' | 'lg';
  config?: TableConfig;
  exportType?: "csv" | "excel";
  buttonText?: string;
  // Subrow props
  subRowsConfig?: any;
  getSelectedParentRows?: () => Promise<TData[]>;
  getSelectedSubRows?: () => Promise<TData[]>;
  parentCount?: number;
  subrowCount?: number;
  enableCsv?: boolean;
  enableExcel?: boolean;
  subRowExportConfig?: {
    entityName: string;
    columnMapping: Record<string, string>;
    columnWidths: Array<{ wch: number }>;
    headers: string[];
    transformFunction?: DataTransformFunction<TData>;
  };
}

export function DataTableExport<TData extends ExportableData>({
  table,
  data,
  selectedData,
  getSelectedItems,
  getAllItems,
  entityName = "items",
  columnMapping,
  columnWidths,
  headers,
  transformFunction,
  size = 'default',
  config,
  exportType,
  buttonText,
  subRowsConfig,
  getSelectedParentRows,
  getSelectedSubRows,
  parentCount = 0,
  subrowCount = 0,
  enableCsv = true,
  enableExcel = true,
  subRowExportConfig,
}: DataTableExportProps<TData>): JSX.Element {
  const [isLoading, setIsLoading] = useState(false);

  // Export parent rows only
  const handleExportParents = async (type: "csv" | "excel") => {
    if (isLoading || !getSelectedParentRows) return;

    try {
      setIsLoading(true);

      // Show loading toast for fetching
      toast.loading("Preparing export...", {
        description: "Fetching selected parent rows...",
        id: "export-parents-toast",
      });

      const parentData = await getSelectedParentRows();

      if (parentData.length === 0) {
        toast.error("No parent rows selected", { id: "export-parents-toast" });
        return;
      }

      // Update toast for processing
      toast.loading("Processing data...", {
        description: "Generating export file...",
        id: "export-parents-toast",
      });

      const success = type === "csv"
        ? exportToCSV(parentData, `${entityName}-parents-export-${Date.now()}`, headers, columnMapping, transformFunction)
        : await exportToExcel(parentData, `${entityName}-parents-export-${Date.now()}`, columnMapping, columnWidths, headers, transformFunction);

      if (success) {
        toast.success(`Exported ${parentData.length} parent rows`, { id: "export-parents-toast" });
      }
    } catch (error) {
      console.error("Error exporting parents:", error);
      toast.error("Export failed", { id: "export-parents-toast" });
    } finally {
      setIsLoading(false);
    }
  };

  // Export subrows only
  const handleExportSubrows = async (type: "csv" | "excel") => {
    if (isLoading || !getSelectedSubRows || !subRowExportConfig) return;

    try {
      setIsLoading(true);

      // Show loading toast for fetching
      toast.loading("Preparing export...", {
        description: "Fetching selected subrows...",
        id: "export-subrows-toast",
      });

      const subrowData = await getSelectedSubRows();

      if (subrowData.length === 0) {
        toast.error("No subrows selected", { id: "export-subrows-toast" });
        return;
      }

      // Update toast for processing
      toast.loading("Processing data...", {
        description: "Generating export file...",
        id: "export-subrows-toast",
      });

      const success = type === "csv"
        ? exportToCSV(subrowData, `${subRowExportConfig.entityName}-export-${Date.now()}`, subRowExportConfig.headers, subRowExportConfig.columnMapping, subRowExportConfig.transformFunction)
        : await exportToExcel(subrowData, `${subRowExportConfig.entityName}-export-${Date.now()}`, subRowExportConfig.columnMapping, subRowExportConfig.columnWidths, subRowExportConfig.headers, subRowExportConfig.transformFunction);

      if (success) {
        toast.success(`Exported ${subrowData.length} subrows`, { id: "export-subrows-toast" });
      }
    } catch (error) {
      console.error("Error exporting subrows:", error);
      toast.error("Export failed", { id: "export-subrows-toast" });
    } finally {
      setIsLoading(false);
    }
  };

  const handleExport = async (type: "csv" | "excel") => {
    if (isLoading) return; // Prevent multiple export requests

    // Create a data fetching function based on the current state
    const fetchExportData = async (): Promise<TData[]> => {
      // If we have selected items and a function to get their complete data
      if (getSelectedItems && selectedData && selectedData.length > 0) {
        // Check if data is on current page or needs to be fetched
        if (selectedData.some(item => Object.keys(item).length === 0)) {
          // We have placeholder data, need to fetch complete data
          toast.loading("Preparing export...", {
            description: `Fetching complete data for selected ${entityName}.`,
            id: "export-data-toast",
          });
        }

        // Fetch complete data for selected items
        const selectedItems = await getSelectedItems();

        if (selectedItems.length === 0) {
          throw new Error(`Failed to retrieve complete data for selected ${entityName}`);
        }

        // Order the items according to the current sorting in the table
        // This preserves the table's page order in the exported data
        const sortedItems = [...selectedItems];
        const sorting = table.getState().sorting;

        if (sorting.length > 0) {
          const { id: sortField, desc: isDescending } = sorting[0];

          // Check if array is empty before accessing first item
          if (sortedItems.length === 0) {
            return sortedItems;
          }

          // Validate that sortField exists in data before sorting
          const sampleItem = sortedItems[0];
          if (!(sortField in sampleItem)) {
            console.warn(`Sort field "${sortField}" not found in data. Skipping sort.`);
            return sortedItems;
          }

          sortedItems.sort((a, b) => {
            try {
              const valueA = a[sortField as keyof TData];
              const valueB = b[sortField as keyof TData];

              if (valueA === valueB) return 0;

              if (valueA === null || valueA === undefined) return isDescending ? 1 : -1;
              if (valueB === null || valueB === undefined) return isDescending ? -1 : 1;

              if (typeof valueA === 'string' && typeof valueB === 'string') {
              return isDescending 
                ? valueB.localeCompare(valueA) 
                : valueA.localeCompare(valueB);
            }

              // For numeric and other comparable types
              return isDescending 
                ? (valueB > valueA ? 1 : -1) 
                : (valueA > valueB ? 1 : -1);
            } catch (sortError) {
              console.error('Error during sorting:', sortError);
              return 0; // Maintain original order on error
            }
          });
        }

        return sortedItems;
      } else if (getAllItems && !selectedData?.length) {
        // If we're exporting all data and have a method to get it with proper ordering
        toast.loading("Preparing export...", {
          description: `Fetching all ${entityName} with current sorting...`,
          id: "export-data-toast",
        });

        // Fetch all data with server-side sorting applied
        const allItems = await getAllItems();

        if (allItems.length === 0) {
          throw new Error(`No ${entityName} available to export`);
        }

        return allItems;
      } else {
        // Otherwise use the provided data (current page data)
        if (!data || data.length === 0) {
          throw new Error("No data available for export");
        }
        return selectedData && selectedData.length > 0 ? selectedData : data;
      }
    };

    try {
      // Get visible columns from the table
      const visibleColumns = table.getAllColumns()
        .filter(column => column.getIsVisible())
        .filter(column => column.id !== 'actions' && column.id !== 'select');

      // Generate export options based on visible columns and respect column order
      const columnOrder = table.getState().columnOrder;
      const orderedVisibleColumns = columnOrder.length > 0
        ? [...visibleColumns].sort((a, b) => {
            const aIndex = columnOrder.indexOf(a.id);
            const bIndex = columnOrder.indexOf(b.id);
            // If column isn't in the order array, put it at the end
            if (aIndex === -1) return 1;
            if (bIndex === -1) return -1;
            return aIndex - bIndex;
          })
        : visibleColumns;

      // Generate export headers - always start with visible columns only
      const visibleColumnIds = orderedVisibleColumns.map(column => column.id);
      // Get all table column IDs (visible + hidden) to identify what's a table column vs transform column
      const allTableColumnIds = table.getAllColumns()
        .filter(column => column.id !== 'actions' && column.id !== 'select')
        .map(column => column.id);

      let exportHeaders: string[];

      if (config?.allowExportNewColumns === false) {
        // Only export visible columns - no new columns from transform
        exportHeaders = headers && headers.length > 0 
          ? headers.filter(header => visibleColumnIds.includes(header))
          : visibleColumnIds;
      } else {
        // Allow new columns from transform function, but still filter existing columns by visibility
        if (headers && headers.length > 0) {
          // Split headers into existing table columns (must be visible) and new transform columns (allowed)
          const existingHeaders = headers.filter(header => allTableColumnIds.includes(header) && visibleColumnIds.includes(header));
          const newHeaders = headers.filter(header => !allTableColumnIds.includes(header));
          exportHeaders = [...existingHeaders, ...newHeaders];
        } else {
          exportHeaders = visibleColumnIds;
        }
      }

      // Auto-generate column mapping from table headers if not provided
      const exportColumnMapping = columnMapping || (() => {
        const mapping: Record<string, string> = {};
        orderedVisibleColumns.forEach(column => {
          // Try to get header text if available
          const headerText = column.columnDef.header as string;

          if (headerText && typeof headerText === 'string') {
            mapping[column.id] = headerText;
          } else {
            // Fallback to formatted column ID
            mapping[column.id] = column.id
              .split(/(?=[A-Z])|_/)
              .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
              .join(' ');
          }
        });
        return mapping;
      })();

      // Filter column widths to match export headers
      const exportColumnWidths = columnWidths ? 
        exportHeaders.map((_, index) => columnWidths[index] || { wch: 15 }) :
        exportHeaders.map(() => ({ wch: 15 }));

      // Use the generic export function with proper options
      await exportData(
        type,
        fetchExportData,
        () => setIsLoading(true),
        () => setIsLoading(false),
        {
          entityName,
          headers: exportHeaders,
          columnMapping: exportColumnMapping,
          columnWidths: exportColumnWidths,
          transformFunction
        }
      );
    } catch (error) {
      console.error("Error exporting data:", error);
      toast.error("Export failed", {
        description: "There was a problem exporting. Please try again.",
        id: "export-data-toast"
      });
      setIsLoading(false);
    }
  };

  const exportAllPages = async (type: "csv" | "excel") => {
    if (isLoading || !getAllItems) return;
    setIsLoading(true);

    try {
      // Show toast for long operations
      toast.loading("Preparing export...", {
        description: `Fetching all ${entityName}...`,
        id: "export-data-toast"
      });

      // Fetch all data with server-side sorting
      const allData = await getAllItems();

      if (allData.length === 0) {
        toast.error("Export failed", {
          description: "No data available to export.",
          id: "export-data-toast"
        });
        return;
      }

      // Get visible columns and apply export
      const visibleColumns = table.getAllColumns()
        .filter(column => column.getIsVisible())
        .filter(column => column.id !== 'actions' && column.id !== 'select');

      // Generate export headers - always start with visible columns only
      const visibleColumnIds = visibleColumns.map(column => column.id);
      // Get all table column IDs (visible + hidden) to identify what's a table column vs transform column
      const allTableColumnIds = table.getAllColumns()
        .filter(column => column.id !== 'actions' && column.id !== 'select')
        .map(column => column.id);

      let exportHeaders: string[];

      if (config?.allowExportNewColumns === false) {
        // Only export visible columns - no new columns from transform
        exportHeaders = headers && headers.length > 0 
          ? headers.filter(header => visibleColumnIds.includes(header))
          : visibleColumnIds;
      } else {
        // Allow new columns from transform function, but still filter existing columns by visibility
        if (headers && headers.length > 0) {
          // Split headers into existing table columns (must be visible) and new transform columns (allowed)
          const existingHeaders = headers.filter(header => allTableColumnIds.includes(header) && visibleColumnIds.includes(header));
          const newHeaders = headers.filter(header => !allTableColumnIds.includes(header));
          exportHeaders = [...existingHeaders, ...newHeaders];
        } else {
          exportHeaders = visibleColumnIds;
        }
      }
      const exportColumnMapping = columnMapping || (() => {
        const mapping: Record<string, string> = {};

        // If we have custom headers, generate mapping for them
        if (headers && headers.length > 0) {
          headers.forEach(header => {
            mapping[header] = header
              .split(/(?=[A-Z])|_/)
              .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
              .join(' ');
          });
        } else {
          // Otherwise use visible columns
          visibleColumns.forEach(column => {
            const headerText = column.columnDef.header as string;

            if (headerText && typeof headerText === 'string') {
              mapping[column.id] = headerText;
            } else {
              mapping[column.id] = column.id
                .split(/(?=[A-Z])|_/)
                .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
                .join(' ');
            }
          });
        }

        return mapping;
      })();

      const exportColumnWidths = columnWidths ?
        exportHeaders.map((_, index) => columnWidths[index] || { wch: 15 }) :
        exportHeaders.map(() => ({ wch: 15 }));

      // Update toast for processing
      toast.loading("Processing data...", {
        description: "Generating export file...",
        id: "export-data-toast"
      });

      // Generate timestamp for filename
      const timestamp = new Date().toISOString().replace(/[:.]/g, "-");
      const filename = `${entityName}-all-pages-export-${timestamp}`;

      // Export based on type
      let success = false;
      if (type === "csv") {
        success = exportToCSV(allData, filename, exportHeaders, exportColumnMapping, transformFunction);
      } else {
        success = await exportToExcel(
          allData,
          filename,
          exportColumnMapping,
          exportColumnWidths,
          exportHeaders,
          transformFunction
        );
      }

      if (success) {
        toast.success("Export successful", {
          description: `Exported all ${allData.length} ${entityName} to ${type.toUpperCase()}.`,
          id: "export-data-toast"
        });
      }
    } catch (error) {
      console.error("Error exporting all pages:", error);
      toast.error("Export failed", {
        description: "There was a problem exporting all pages. Please try again.",
        id: "export-data-toast"
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Check if any rows are selected
  const hasSelection = selectedData && selectedData.length > 0;

  // If exportType and buttonText are provided, show single button
  if (exportType && buttonText) {
    return (
      <Button
        variant="outline"
        size={size}
        disabled={isLoading}
        onClick={() => handleExport(exportType)}
      >
        {isLoading ? (
          <>
            <Loader2 className="mr-2 h-4 w-4 animate-spin" />
            Exporting...
          </>
        ) : (
          <>
            <DownloadIcon className="mr-2 h-4 w-4" />
            {buttonText}
          </>
        )}
      </Button>
    );
  }

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="outline" size={size} disabled={isLoading}>
          {isLoading ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              Exporting...
            </>
          ) : (
            <>
              <DownloadIcon className="mr-2 h-4 w-4" />
              Export
              {hasSelection && subRowsConfig?.enabled && (
                <span className="ml-1">({parentCount + subrowCount})</span>
              )}
              {hasSelection && !subRowsConfig?.enabled && (
                <span className="ml-1">({selectedData?.length})</span>
              )}
            </>
          )}
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end">
        {hasSelection && subRowsConfig?.enabled ? (
          // Subrow tables: Show 4 options
          <>
            {parentCount > 0 && enableCsv && (
              <DropdownMenuItem onClick={() => handleExportParents("csv")} disabled={isLoading}>
                Export Parents as CSV ({parentCount})
              </DropdownMenuItem>
            )}
            {parentCount > 0 && enableExcel && (
              <DropdownMenuItem onClick={() => handleExportParents("excel")} disabled={isLoading}>
                Export Parents as Excel ({parentCount})
              </DropdownMenuItem>
            )}
            {subrowCount > 0 && enableCsv && (
              <DropdownMenuItem onClick={() => handleExportSubrows("csv")} disabled={isLoading}>
                Export Subrows as CSV ({subrowCount})
              </DropdownMenuItem>
            )}
            {subrowCount > 0 && enableExcel && (
              <DropdownMenuItem onClick={() => handleExportSubrows("excel")} disabled={isLoading}>
                Export Subrows as Excel ({subrowCount})
              </DropdownMenuItem>
            )}
          </>
        ) : hasSelection ? (
          // Normal tables: Show 2 options
          <>
            <DropdownMenuItem onClick={() => handleExport("csv")} disabled={isLoading}>
              Export Selected as CSV
            </DropdownMenuItem>
            <DropdownMenuItem onClick={() => handleExport("excel")} disabled={isLoading}>
              Export Selected as XLS
            </DropdownMenuItem>
          </>
        ) : (
          <>
            <DropdownMenuItem onClick={() => handleExport("csv")} disabled={isLoading}>
              Export Current Page as CSV
            </DropdownMenuItem>
            <DropdownMenuItem onClick={() => handleExport("excel")} disabled={isLoading}>
              Export Current Page as XLS
            </DropdownMenuItem>
            {getAllItems && (
              <>
                <DropdownMenuItem 
                  onClick={() => exportAllPages("csv")} 
                  disabled={isLoading}
                >
                  Export All Pages as CSV
                </DropdownMenuItem>
                <DropdownMenuItem 
                  onClick={() => exportAllPages("excel")} 
                  disabled={isLoading}
                >
                  Export All Pages as XLS
                </DropdownMenuItem>
              </>
            )}
          </>
        )}
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

"use client"

// ** import types
import type { Header, Table } from "@tanstack/react-table"

// ** import core packages
import React from "react"
import { GripVertical } from "lucide-react"

// ** import components
import { Separator } from "@/components/ui/separator"

// ** import utils
import { cn } from "@/lib/utils"

export function DataTableResizer<TData>({
  header,
  table,
}: {
  header: Header<TData, unknown>
  table: Table<TData>
}) {
  const isResizing = header.column.getIsResizing()

  return (
    <div
      onMouseDown={header.getResizeHandler()}
      onTouchStart={header.getResizeHandler()}
      className={cn(
        "absolute right-0 top-0 flex h-full w-4 cursor-col-resize select-none touch-none items-center justify-center",
        "opacity-0 group-hover/th:opacity-100 z-10",
        isResizing && "opacity-100"
      )}
      aria-hidden="true"
      data-resizing={isResizing ? "true" : undefined}
    >
      <div className="flex h-4/5 items-center justify-center">
        <Separator
          orientation="vertical"
          decorative={false}
          className={cn(
            "h-4/5 w-0.5 transition-colors duration-200",
            isResizing ? 
              "bg-primary" : 
              "bg-border"
          )}
        />

        {/* Use the GripVertical icon for better visual indication */}
        <GripVertical 
          className={cn(
            "absolute h-4 w-4 text-muted-foreground/70",
            isResizing ? "text-primary" : "text-muted-foreground/70"
          )}
          strokeWidth={1.5}
        />
      </div>
    </div>
  )
}

"use client";

// ** import types
import type { Row } from "@tanstack/react-table";

// ** import core packages
import { ChevronRight } from "lucide-react";

// ** import utils
import { cn } from "@/lib/utils";

interface ExpandIconProps<TData> {
  row: Row<TData>;
  className?: string;
  hideWhenSingle?: boolean;
}

export function ExpandIcon<TData>({
  row,
  className,
  hideWhenSingle = false
}: ExpandIconProps<TData>) {
  // Check if row can be expanded
  if (!row.getCanExpand()) {
    return <div className="w-4" />; // Spacer for alignment
  }

  // Hide icon if only 1 subrow and hideWhenSingle is true
  if (hideWhenSingle && row.subRows && row.subRows.length === 1) {
    return <div className="w-4" />;
  }

  return (
    <button
      onClick={row.getToggleExpandedHandler()}
      className={cn(
        "inline-flex items-center justify-center",
        "h-6 w-6 cursor-pointer transition-all",
        "hover:bg-muted rounded-sm",
        "focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring",
        className
      )}
      aria-label={row.getIsExpanded() ? "Collapse row" : "Expand row"}
      aria-expanded={row.getIsExpanded()}
      type="button"
    >
      <ChevronRight
        className={cn(
          "h-4 w-4 transition-transform duration-200",
          row.getIsExpanded() && "rotate-90"
        )}
      />
    </button>
  );
}

"use client";

import {
  ChevronLeftIcon,
  ChevronRightIcon,
  DoubleArrowLeftIcon,
  DoubleArrowRightIcon,
} from "@radix-ui/react-icons";
import type { Table } from "@tanstack/react-table";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

const getButtonSizeClass = (size: 'sm' | 'default' | 'lg') => {
  switch (size) {
    case 'sm': return 'h-7 w-7 p-0';
    case 'lg': return 'h-11 w-11 p-0';
    default: return 'h-8 w-8 p-0';
  }
};

interface DataTablePaginationProps<TData> {
  table: Table<TData>;
  totalItems?: number; // Total number of items from API
  totalSelectedItems?: number; // Total selected items across all pages
  pageSizeOptions?: number[]; // Custom page size options
  size?: 'sm' | 'default' | 'lg'; // Size prop for components
}

export function DataTablePagination<TData>({
  table,
  totalItems = 0,
  totalSelectedItems = 0,
  pageSizeOptions = [10, 20, 30, 40, 50], // Default options if none provided
  size = 'default'
}: DataTablePaginationProps<TData>) {
  // Convert 'lg' size to 'default' for SelectTrigger since it only accepts 'sm' | 'default'
  const selectSize = size === 'lg' ? 'default' : size;

  return (
    <div className="flex w-full flex-col items-center justify-between gap-4 overflow-auto px-2 py-1 sm:flex-row sm:gap-8">
      <div className="flex-1 text-sm text-muted-foreground">
        {totalSelectedItems} of {totalItems} row(s) selected.
      </div>
      <div className="flex flex-col items-center gap-4 sm:flex-row sm:gap-6 lg:gap-8">
        <div className="flex items-center space-x-2">
          <p className="whitespace-nowrap text-sm font-medium">
            Rows per page
          </p>
          <Select
            value={`${table.getState().pagination.pageSize}`}
            onValueChange={(value) => {
              // Validate the input value
              const numericValue = parseInt(value, 10);
              if (isNaN(numericValue) || numericValue <= 0) {
                console.error(`Invalid page size value: ${value}`);
                return;
              }

              try {
                // Force URL update via direct window manipulation first
                // This ensures the URL gets updated before the table state changes
                const url = new URL(window.location.href);
                url.searchParams.set('pageSize', value);
                url.searchParams.set('page', '1'); // Always reset to page 1
                window.history.replaceState({}, '', url.toString());

                // Then use the table's pagination change handler to update table state
                // This order ensures the URL is already set when the table state updates
                table.setPagination({
                  pageIndex: 0, // Reset to first page
                  pageSize: numericValue
                });
              } catch (error) {
                console.error('Error updating pagination:', error);
              }
            }}
          >
            <SelectTrigger className="cursor-pointer" size={selectSize}>
              <SelectValue placeholder={table.getState().pagination.pageSize} />
            </SelectTrigger>
            <SelectContent side="top" className="cursor-pointer">
              {pageSizeOptions.map((pageSize) => (
                <SelectItem key={pageSize} value={`${pageSize}`} className="cursor-pointer">
                  {pageSize}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
        <div className="flex items-center justify-center text-sm font-medium">
          Page {table.getState().pagination.pageIndex + 1} of{" "}
          {table.getPageCount() || 1}
        </div>
        <div className="flex items-center space-x-2">
          <Button
            aria-label="Go to first page"
            variant="outline"
            className={`${getButtonSizeClass(size)} hidden lg:flex cursor-pointer`}
            onClick={() => table.setPagination({ pageIndex: 0, pageSize: table.getState().pagination.pageSize })}
            disabled={!table.getCanPreviousPage()}
          >
            <DoubleArrowLeftIcon className="h-4 w-4" aria-hidden="true" />
          </Button>
          <Button
            aria-label="Go to previous page"
            variant="outline"
            className={`${getButtonSizeClass(size)} cursor-pointer`}
            onClick={() => table.setPagination({
              pageIndex: table.getState().pagination.pageIndex - 1,
              pageSize: table.getState().pagination.pageSize
            })}
            disabled={!table.getCanPreviousPage()}
          >
            <ChevronLeftIcon className="h-4 w-4" aria-hidden="true" />
          </Button>
          <Button
            aria-label="Go to next page"
            variant="outline"
            className={`${getButtonSizeClass(size)} cursor-pointer`}
            onClick={() => table.setPagination({
              pageIndex: table.getState().pagination.pageIndex + 1,
              pageSize: table.getState().pagination.pageSize
            })}
            disabled={!table.getCanNextPage()}
          >
            <ChevronRightIcon className="h-4 w-4" aria-hidden="true" />
          </Button>
          <Button
            aria-label="Go to last page"
            variant="outline"
            className={`${getButtonSizeClass(size)} hidden lg:flex cursor-pointer`}
            onClick={() => table.setPagination({
              pageIndex: table.getPageCount() - 1,
              pageSize: table.getState().pagination.pageSize
            })}
            disabled={!table.getCanNextPage()}
          >
            <DoubleArrowRightIcon className="h-4 w-4" aria-hidden="true" />
          </Button>
        </div>
      </div>
    </div>
  );
}

"use client";

import { Cross2Icon } from "@radix-ui/react-icons";
import type { Table } from "@tanstack/react-table";
import { useEffect, useState, useRef, useCallback } from "react";
import { Settings, Undo2, TrashIcon, EyeOff, CheckSquare, MoveHorizontal } from "lucide-react";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { CalendarDatePicker } from "@/components/calendar-date-picker";
import { DataTableViewOptions } from "./view-options";
import { DataTableExport } from "./data-export";
import type { DataTransformFunction, ExportableData } from "./utils/export-utils";
import { resetUrlState } from "./utils/deep-utils";
import { parseDateFromUrl } from "./utils/url-state";
import type { TableConfig } from "./utils/table-config";
import { formatDate } from "./utils/date-format";

// Helper functions for component sizing
const getInputSizeClass = (size: 'sm' | 'default' | 'lg') => {
  switch (size) {
    case 'sm': return 'h-8';
    case 'lg': return 'h-11';
    default: return '';
  }
};

const getButtonSizeClass = (size: 'sm' | 'default' | 'lg', isIcon = false) => {
  if (isIcon) {
    switch (size) {
      case 'sm': return 'h-8 w-8';
      case 'lg': return 'h-11 w-11';
      default: return '';
    }
  }
  switch (size) {
    case 'sm': return 'h-8 px-3';
    case 'lg': return 'h-11 px-5';
    default: return '';
  }
};

interface DataTableToolbarProps<TData extends ExportableData> {
  table: Table<TData>;
  search: string;
  dateRange: { from_date: string; to_date: string };
  setSearch: (value: string | ((prev: string) => string)) => void;
  setDateRange: (
    value:
      | { from_date: string; to_date: string }
      | ((prev: { from_date: string; to_date: string }) => {
        from_date: string;
        to_date: string;
      })
  ) => void;
  totalSelectedItems?: number;
  deleteSelection?: () => void;
  getSelectedItems?: () => Promise<TData[]>;
  getAllItems?: () => TData[];
  config: TableConfig;
  resetColumnSizing?: () => void;
  resetColumnOrder?: () => void;
  entityName?: string;
  columnMapping?: Record<string, string>;
  columnWidths?: Array<{ wch: number }>;
  headers?: string[];
  transformFunction?: DataTransformFunction<TData>;
  customToolbarComponent?: React.ReactNode;
  // Subrow props
  subRowsConfig?: any;
  getSelectedParentsAndSubrows?: () => { parents: TData[]; subrows: any[]; parentIds: any[]; subrowIds: any[] };
  getSelectedParentRows?: () => Promise<TData[]>;
  getSelectedSubRows?: () => Promise<TData[]>;
  totalParentCount?: number;
  totalSubrowCount?: number;
  enableCsv?: boolean;
  enableExcel?: boolean;
  subRowExportConfig?: {
    entityName: string;
    columnMapping: Record<string, string>;
    columnWidths: Array<{ wch: number }>;
    headers: string[];
    transformFunction?: DataTransformFunction<TData>;
  };
}

export function DataTableToolbar<TData extends ExportableData>({
  table,
  search,
  dateRange,
  setSearch,
  setDateRange,
  totalSelectedItems = 0,
  deleteSelection,
  getSelectedItems,
  getAllItems,
  config,
  resetColumnSizing,
  resetColumnOrder,
  entityName = "items",
  columnMapping,
  columnWidths,
  headers,
  transformFunction,
  customToolbarComponent,
  subRowsConfig,
  getSelectedParentsAndSubrows,
  getSelectedParentRows,
  getSelectedSubRows,
  totalParentCount: totalParentCountProp,
  totalSubrowCount: totalSubrowCountProp,
  enableCsv = true,
  enableExcel = true,
  subRowExportConfig,
}: DataTableToolbarProps<TData>) {
  const tableFiltered = table.getState().columnFilters.length > 0;

  // Calculate parent and subrow selections
  const { parents = [], subrows = [] } = subRowsConfig?.enabled && getSelectedParentsAndSubrows
    ? getSelectedParentsAndSubrows()
    : { parents: [], subrows: [] };

  // Use prop counts if available (cross-page), otherwise use current page counts
  const parentCount = totalParentCountProp !== undefined ? totalParentCountProp : parents.length;
  const subrowCount = totalSubrowCountProp !== undefined ? totalSubrowCountProp : subrows.length;

  // Get search value from table state as fallback
  const currentSearchFromTable =
    (table.getState().globalFilter as string) || "";

  // Local (debounced) search input state. Source of truth is the parent `search` state.
  const [localSearch, setLocalSearch] = useState(search || currentSearchFromTable);

  // Track if the search is being updated locally
  const isLocallyUpdatingSearch = useRef(false);

  // Update local search when external state changes (URL state / parent state)
  useEffect(() => {
    // Skip if local update is in progress
    if (isLocallyUpdatingSearch.current) {
      return;
    }
    if (search !== localSearch) {
      setLocalSearch(search);
    }
  }, [search, localSearch]);

  const tableSearch = (table.getState().globalFilter as string) || "";
  // Also update local search when table globalFilter changes
  useEffect(() => {
    // Skip if local update is in progress
    if (isLocallyUpdatingSearch.current) {
      return;
    }

    if (tableSearch !== localSearch && tableSearch !== "") {
      setLocalSearch(tableSearch);
    }
  }, [tableSearch, localSearch]);

  // Reference to track if we're currently updating dates
  const isUpdatingDates = useRef(false);

  // Reference to track the last set date values to prevent updates with equal values
  const lastSetDates = useRef<{
    from: Date | undefined;
    to: Date | undefined;
  }>({ from: undefined, to: undefined });

  // Memoize the getInitialDates function to prevent unnecessary recreations
  const getInitialDates = useCallback((): {
    from: Date | undefined;
    to: Date | undefined;
  } => {
    // If we're in the middle of an update, don't re-parse to avoid cycles
    if (isUpdatingDates.current) {
      return lastSetDates.current;
    }

    const fromDate = dateRange?.from_date ? parseDateFromUrl(dateRange.from_date) : undefined;
    const toDate = dateRange?.to_date ? parseDateFromUrl(dateRange.to_date) : undefined;

    lastSetDates.current = { from: fromDate, to: toDate };

    return { from: fromDate, to: toDate };
  }, [dateRange?.from_date, dateRange?.to_date]);

  // Initial state with date values from URL
  const [dates, setDates] = useState<{
    from: Date | undefined;
    to: Date | undefined;
  }>(getInitialDates());

  // Track if user has explicitly changed dates
  const [datesModified, setDatesModified] = useState(
    !!dates.from || !!dates.to
  );

  // Determine if any filters are active
  const isFiltered = tableFiltered || !!localSearch || datesModified;

  // Create a ref to store the debounce timer
  const searchDebounceTimerRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  // Cleanup timers when component unmounts
  useEffect(() => {
    return () => {
      // Clear debounce timer
      if (searchDebounceTimerRef.current) {
        clearTimeout(searchDebounceTimerRef.current);
      }
    };
  }, []);

  // Handle search with improved debounce to prevent character loss
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;

    // Mark that search is being updated locally
    isLocallyUpdatingSearch.current = true;
    setLocalSearch(value);

    // Clear any existing timer to prevent race conditions
    if (searchDebounceTimerRef.current) {
      clearTimeout(searchDebounceTimerRef.current);
    }

    // Set a new debounce timer to update the actual search state
    searchDebounceTimerRef.current = setTimeout(() => {
      // Trim whitespace before sending to backend API
      const trimmedValue = value.trim();
      setSearch(trimmedValue);
      searchDebounceTimerRef.current = null;

      // Reset the local update flag after a short delay
      // This ensures URL changes don't override the input immediately
      setTimeout(() => {
        isLocallyUpdatingSearch.current = false;
      }, 100);
    }, 500);
  };

  // Listen for URL parameter changes and update local state if needed
  useEffect(() => {
    // Skip this effect if we're currently updating the dates ourselves
    if (isUpdatingDates.current) {
      return;
    }

    const newDates = getInitialDates();

    // Check if dates have actually changed to avoid unnecessary updates
    const hasFromChanged = 
      (newDates.from && !dates.from) || 
      (!newDates.from && dates.from) || 
      (newDates.from && dates.from && newDates.from.getTime() !== dates.from.getTime());

    const hasToChanged = 
      (newDates.to && !dates.to) || 
      (!newDates.to && dates.to) || 
      (newDates.to && dates.to && newDates.to.getTime() !== dates.to.getTime());

    if (hasFromChanged || hasToChanged) {
      setDates(newDates);
      setDatesModified(!!(newDates.from || newDates.to));
    }
  }, [dates, getInitialDates]);

  // Handle date selection for filtering
  const handleDateSelect = ({ from, to }: { from: Date; to: Date }) => {
    // Compare with previous dates to avoid unnecessary updates
    const hasFromChanged = 
      (from && !dates.from) || 
      (!from && dates.from) || 
      (from && dates.from && from.getTime() !== dates.from.getTime());

    const hasToChanged = 
      (to && !dates.to) || 
      (!to && dates.to) || 
      (to && dates.to && to.getTime() !== dates.to.getTime());

    // Only update if dates have actually changed
    if (!hasFromChanged && !hasToChanged) {
      return;
    }

    // Set flag to prevent update loops
    isUpdatingDates.current = true;

    // Update internal state
    setDates({ from, to });
    setDatesModified(true);
    lastSetDates.current = { from, to };

    // Convert dates to strings in YYYY-MM-DD format for the API
    setDateRange({
      from_date: from ? formatDate(from) : "",
      to_date: to ? formatDate(to) : "",
    });

    // Reset the updating flag after a delay
    setTimeout(() => {
      isUpdatingDates.current = false;
    }, 100);
  };

  // Reset all filters and URL state
  const handleResetFilters = () => {
    // Reset table filters
    table.resetColumnFilters();

    // Cancel any in-flight debounced search update so reset can't be overridden later.
    if (searchDebounceTimerRef.current) {
      clearTimeout(searchDebounceTimerRef.current);
      searchDebounceTimerRef.current = null;
    }
    isLocallyUpdatingSearch.current = false;

    // Reset search
    setLocalSearch("");
    setSearch("");

    // Reset dates to undefined (no filter)
    setDates({
      from: undefined,
      to: undefined,
    });
    setDatesModified(false);
    setDateRange({
      from_date: "",
      to_date: "",
    });

    // Reset URL state by removing all query parameters, but only if URL state is enabled
    if (config.enableUrlState) {
      resetUrlState();
    }
  };

  // Get selected items data for export - this is now just for the UI indication
  // The actual data fetching happens in the export component
  const selectedItems =
    totalSelectedItems > 0
      ? new Array(totalSelectedItems).fill({} as TData)
      : [];

  // Get all available items data for export
  const allItems = getAllItems ? getAllItems() : [];

  return (
    <div className="flex flex-wrap items-center justify-between">
      <div className="flex flex-1 flex-wrap items-center gap-2">
        {config.enableSearch && (
          <Input
            placeholder={config.searchPlaceholder || `Search ${entityName}...`}
            value={localSearch}
            onChange={handleSearchChange}
            className={`w-[150px] lg:w-[250px] ${getInputSizeClass(config.size)}`}
          />
        )}

        {config.enableDateFilter && (
          <div className="flex items-center">
            <CalendarDatePicker
              date={{
                from: dates.from,
                to: dates.to,
              }}
              onDateSelect={handleDateSelect}
              className={`w-fit cursor-pointer ${getInputSizeClass(config.size)}`}
              variant="outline"
            />
          </div>
        )}

        {isFiltered && (
          <Button
            variant="ghost"
            onClick={handleResetFilters}
            className={getButtonSizeClass(config.size)}
          >
            Reset
            <Cross2Icon className="ml-2 h-4 w-4" />
          </Button>
        )}
      </div>

      <div className="flex items-center gap-2">
        {customToolbarComponent}

        {config.enableExport && (
          <DataTableExport
            table={table}
            data={allItems}
            selectedData={selectedItems}
            getSelectedItems={getSelectedItems}
            entityName={entityName}
            columnMapping={columnMapping}
            columnWidths={columnWidths}
            headers={headers}
            transformFunction={transformFunction}
            size={config.size}
            config={config}
            // Subrow props
            subRowsConfig={subRowsConfig}
            getSelectedParentRows={getSelectedParentRows}
            getSelectedSubRows={getSelectedSubRows}
            parentCount={parentCount}
            subrowCount={subrowCount}
            enableCsv={enableCsv}
            enableExcel={enableExcel}
            subRowExportConfig={subRowExportConfig}
          />
        )}

        {config.enableColumnVisibility && (
          <DataTableViewOptions
            table={table}
            columnMapping={columnMapping}
            size={config.size}
          />
        )}

        <Popover>
          <PopoverTrigger asChild>
            <Button
              variant="outline"
              size="icon"
              className={getButtonSizeClass(config.size, true)}
              title="Table Settings"
            >
              <Settings className="h-4 w-4" />
              <span className="sr-only">Open table settings</span>
            </Button>
          </PopoverTrigger>
          <PopoverContent className="w-60" align="end">
            <div className="grid gap-4">
              <div className="space-y-2">
                <h4 className="font-medium leading-none">Table Settings</h4>
              </div>

              <div className="grid gap-2">
                {config.enableColumnResizing && resetColumnSizing && (
                  <Button
                    variant="outline"
                    size={config.size}
                    className="justify-start"
                    onClick={(e) => {
                      e.preventDefault();
                      resetColumnSizing();
                    }}
                  >
                    <Undo2 className="mr-2 h-4 w-4" />
                    Reset Column Sizes
                  </Button>
                )}

                {resetColumnOrder && (
                  <Button
                    variant="outline"
                    size={config.size}
                    className="justify-start"
                    onClick={(e) => {
                      e.preventDefault();
                      resetColumnOrder();
                    }}
                  >
                    <MoveHorizontal className="mr-2 h-4 w-4" />
                    Reset Column Order
                  </Button>
                )}

                {config.enableRowSelection && (
                  <Button
                    variant="outline"
                    size={config.size}
                    className="justify-start"
                    onClick={(e) => {
                      e.preventDefault();
                      table.resetRowSelection();
                      // Also call the parent component's deleteSelection function if available
                      if (deleteSelection) {
                        deleteSelection();
                      }
                    }}
                  >
                    <CheckSquare className="mr-2 h-4 w-4" />
                    Clear Selection
                  </Button>
                )}

                {!table.getIsAllColumnsVisible() && (
                  <Button
                    variant="outline"
                    size={config.size}
                    className="justify-start"
                    onClick={() => table.resetColumnVisibility()}
                  >
                    <EyeOff className="mr-2 h-4 w-4" />
                    Show All Columns
                  </Button>
                )}
              </div>
            </div>
          </PopoverContent>
        </Popover>
      </div>
    </div>
  );
}

"use client";

import type { Table, Column } from "@tanstack/react-table";
import { Check, GripVertical, Settings2, RotateCcw } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
  CommandSeparator,
} from "@/components/ui/command";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { cn } from "@/lib/utils";
import * as React from "react";
import { useCallback, useEffect, useState, useMemo } from "react";

interface DataTableViewOptionsProps<TData> {
  table: Table<TData>;
  columnMapping?: Record<string, string>;
  size?: 'sm' | 'default' | 'lg';
}

// Local storage key for column order
const COLUMN_ORDER_STORAGE_KEY = "data-table-column-order";

export function DataTableViewOptions<TData>({
  table,
  columnMapping,
  size = 'default',
}: DataTableViewOptionsProps<TData>) {
  // Get columns that can be hidden
  const columns = React.useMemo(
    () =>
      table
        .getAllColumns()
        .filter(
          (column) =>
            typeof column.accessorFn !== "undefined" && column.getCanHide(),
        ),
    [table],
  );

  // State for drag and drop
  const [draggedColumnId, setDraggedColumnId] = useState<string | null>(null);

  // Order columns based on the current table column order
  const columnOrder = table.getState().columnOrder;
  const orderedColumns = useMemo(() => {
    if (!columnOrder.length) {
      return columns;
    }

    // Create a new array with columns sorted according to the columnOrder
    return [...columns].sort((a, b) => {
      const aIndex = columnOrder.indexOf(a.id);
      const bIndex = columnOrder.indexOf(b.id);

      // If column isn't in the order array, put it at the end
      if (aIndex === -1) return 1;
      if (bIndex === -1) return -1;

      return aIndex - bIndex;
    });
  }, [columns, columnOrder]);

  // Load column order from localStorage on initial render
  useEffect(() => {
    try {
      const savedOrder = localStorage.getItem(COLUMN_ORDER_STORAGE_KEY);
      if (savedOrder) {
        const columnOrder = JSON.parse(savedOrder);
        // Apply saved column order to the table
        table.setColumnOrder(columnOrder);
      }
    } catch (error) {
      console.error("Error loading column order:", error);
    }
  }, [table]);

  // Save column order to localStorage when it changes
  const saveColumnOrder = useCallback((columnOrder: string[]) => {
    try {
      localStorage.setItem(COLUMN_ORDER_STORAGE_KEY, JSON.stringify(columnOrder));
    } catch (error) {
      console.error("Error saving column order:", error);
    }
  }, []);

  // Handle column visibility toggle
  const handleColumnVisibilityToggle = useCallback((columnId: string) => {
    const currentVisibility = table.getState().columnVisibility;
    const isCurrentlyVisible = currentVisibility[columnId] !== false;

    table.setColumnVisibility({
      ...currentVisibility,
      [columnId]: !isCurrentlyVisible,
    });
  }, [table]);

  // Handle drag start
  const handleDragStart = useCallback((e: React.DragEvent, columnId: string) => {
    setDraggedColumnId(columnId);
    e.dataTransfer.effectAllowed = "move";
    // This helps with dragging visuals
    if (e.dataTransfer.setDragImage && e.currentTarget instanceof HTMLElement) {
      e.dataTransfer.setDragImage(e.currentTarget, 20, 20);
    }
  }, []);

  // Handle drag over
  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = "move";
  }, []);

  // Handle drop
  const handleDrop = useCallback((e: React.DragEvent, targetColumnId: string) => {
    e.preventDefault();

    if (!draggedColumnId || draggedColumnId === targetColumnId) return;

    // Get current column order
    const currentOrder = table.getState().columnOrder.length > 0
      ? [...table.getState().columnOrder]
      : table.getAllLeafColumns().map(d => d.id);

    // Find indices
    const draggedIndex = currentOrder.indexOf(draggedColumnId);
    const targetIndex = currentOrder.indexOf(targetColumnId);

    if (draggedIndex === -1 || targetIndex === -1) return;

    // Create new order by moving the dragged column
    const newOrder = [...currentOrder];
    newOrder.splice(draggedIndex, 1);
    newOrder.splice(targetIndex, 0, draggedColumnId);

    // Update table column order
    table.setColumnOrder(newOrder);

    // Save to localStorage
    saveColumnOrder(newOrder);

    setDraggedColumnId(null);
  }, [draggedColumnId, table, saveColumnOrder]);

  // Reset column order
  const resetColumnOrder = useCallback(() => {
    // Clear order by setting empty array (table will use default order)
    table.setColumnOrder([]);
    // Remove from localStorage
    localStorage.removeItem(COLUMN_ORDER_STORAGE_KEY);
  }, [table]);

  // Get column display label
  const getColumnLabel = useCallback((column: Column<TData, unknown>) => {
    // First check if we have a mapping for this column
    if (columnMapping && column.id in columnMapping) {
      return columnMapping[column.id];
    }
    // Then check for meta label
    return (column.columnDef.meta as { label?: string })?.label ??
      // Finally fall back to formatted column ID
      column.id.replace(/_/g, ' ');
  }, [columnMapping]);

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button
          aria-label="Toggle columns"
          variant="outline" 
          size={size}
          className="ml-auto hidden lg:flex"
        >
          <Settings2 className="mr-2 h-4 w-4" />
          View
        </Button>
      </PopoverTrigger>
      <PopoverContent align="end" className="w-[220px] p-0">
        <Command>
          <CommandInput placeholder="Search columns..." />
          <CommandList>
            <CommandEmpty>No columns found.</CommandEmpty>
            <CommandGroup>
              {orderedColumns.map((column) => (
                <CommandItem
                  key={column.id}
                  onSelect={() => handleColumnVisibilityToggle(column.id)}
                  draggable
                  onDragStart={(e) => handleDragStart(e, column.id)}
                  onDragOver={handleDragOver}
                  onDrop={(e) => handleDrop(e, column.id)}
                  className={cn(
                    "flex items-center cursor-grab",
                    draggedColumnId === column.id && "bg-accent opacity-50"
                  )}
                >
                  <GripVertical className="mr-2 h-4 w-4 cursor-grab" />
                  <span className="flex-grow truncate capitalize">
                    {getColumnLabel(column)}
                  </span>
                  <Check
                    className={cn(
                      "ml-auto h-4 w-4",
                      column.getIsVisible() ? "opacity-100" : "opacity-0",
                    )}
                  />
                </CommandItem>
              ))}
            </CommandGroup>
            <CommandSeparator />
            <CommandGroup>
              <CommandItem
                onSelect={resetColumnOrder}
                className="justify-center text-center cursor-pointer"
              >
                <RotateCcw className="mr-2 h-4 w-4" />
                Reset Column Order
              </CommandItem>
            </CommandGroup>
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

"use client";

import { useState, useEffect, useCallback, useRef } from "react";
import { ColumnSizingState } from "@tanstack/react-table";

// Debounce function to limit expensive operations
function useDebounce<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
}

/**
 * Custom hook to manage table column sizing with localStorage persistence
 * and optimized performance
 * 
 * @param tableId Unique identifier for the table
 * @param enableResizing Whether column resizing is enabled
 * @returns An object with column sizing state and setter
 */
export function useTableColumnResize(
  tableId: string,
  enableResizing: boolean = false
) {
  // Column sizing state
  const [columnSizing, setColumnSizing] = useState<ColumnSizingState>({});

  // Track if initial load from localStorage is complete
  const initialLoadComplete = useRef(false);

  // Track if sizes have been changed by user (to avoid overwriting on initial load)
  const userChangedSizes = useRef(false);

  // Track the previous state to detect actual changes
  const prevSizingRef = useRef<ColumnSizingState>({});

  // Debounce the columnSizing for localStorage operations to improve performance
  const debouncedColumnSizing = useDebounce(columnSizing, 300);

  // Custom setter that marks user changes
  const handleSetColumnSizing = useCallback((newSizing: ColumnSizingState | ((prev: ColumnSizingState) => ColumnSizingState)) => {
    setColumnSizing(prev => {
      const nextState = typeof newSizing === 'function' ? newSizing(prev) : newSizing;

      // Check if this is a real user change and not just the initial load
      if (initialLoadComplete.current && 
          JSON.stringify(nextState) !== JSON.stringify(prevSizingRef.current)) {
        userChangedSizes.current = true;
        prevSizingRef.current = nextState;
      }

      return nextState;
    });
  }, []);

  // Load saved column sizes from localStorage on mount
  useEffect(() => {
    if (enableResizing && !initialLoadComplete.current) {
      try {
        const savedSizing = localStorage.getItem(`table-column-sizing-${tableId}`);
        if (savedSizing) {
          const parsed = JSON.parse(savedSizing);
          setColumnSizing(parsed);
          prevSizingRef.current = parsed;
        }
      } catch (error) {
        console.warn('Failed to load saved column sizing from localStorage:', error);
      } finally {
        initialLoadComplete.current = true;
      }
    }
  }, [tableId, enableResizing]);

  // Save column sizes to localStorage when they change (debounced)
  useEffect(() => {
    if (enableResizing && initialLoadComplete.current && userChangedSizes.current) {
      try {
        localStorage.setItem(`table-column-sizing-${tableId}`, JSON.stringify(debouncedColumnSizing));
      } catch (error) {
        console.warn('Failed to save column sizing to localStorage:', error);
      }
    }
  }, [debouncedColumnSizing, tableId, enableResizing]);

  // Memoized function to reset column sizes
  const resetColumnSizing = useCallback(() => {
    setColumnSizing({});
    userChangedSizes.current = true;
    prevSizingRef.current = {};

    if (enableResizing) {
      try {
        localStorage.removeItem(`table-column-sizing-${tableId}`);
      } catch (error) {
        console.warn('Failed to remove column sizing from localStorage:', error);
      }
    }
  }, [enableResizing, tableId]);

  return {
    columnSizing,
    setColumnSizing: handleSetColumnSizing,
    resetColumnSizing,
  };
}

/**
 * Utility functions for case conversion in table keys
 */

export type CaseFormat = 'snake_case' | 'camelCase' | 'PascalCase' | 'kebab-case';

/**
 * Convert a string to snake_case
 */
export function toSnakeCase(str: string): string {
  return str
    .replace(/([A-Z])/g, '_$1')
    .replace(/[-\s]/g, '_')
    .toLowerCase()
    .replace(/^_/, '');
}

/**
 * Convert a string to camelCase
 */
export function toCamelCase(str: string): string {
  return str
    .toLowerCase()
    .replace(/[-_\s](.)/g, (_, char) => char.toUpperCase());
}

/**
 * Convert a string to PascalCase
 */
export function toPascalCase(str: string): string {
  const camelCase = toCamelCase(str);
  return camelCase.charAt(0).toUpperCase() + camelCase.slice(1);
}

/**
 * Convert a string to kebab-case
 */
export function toKebabCase(str: string): string {
  return str
    .replace(/([A-Z])/g, '-$1')
    .replace(/[_\s]/g, '-')
    .toLowerCase()
    .replace(/^-/, '');
}

/**
 * Convert a string to the specified case format
 */
export function convertCase(str: string, format: CaseFormat): string {
  switch (format) {
    case 'snake_case':
      return toSnakeCase(str);
    case 'camelCase':
      return toCamelCase(str);
    case 'PascalCase':
      return toPascalCase(str);
    case 'kebab-case':
      return toKebabCase(str);
    default:
      return str;
  }
}

/**
 * Type for converted object keys
 */
type ConvertedKeys<T extends Record<string, unknown>> = {
  [K in keyof T as K extends string ? string : never]: T[K];
};

/**
 * Convert all keys in an object to the specified case format
 */
export function convertObjectKeys<T extends Record<string, unknown>>(
  obj: T,
  format: CaseFormat
): ConvertedKeys<T> {
  const result: Record<string, unknown> = {};

  Object.entries(obj).forEach(([key, value]) => {
    const newKey = convertCase(key, format);
    result[newKey] = value;
  });

  return result as ConvertedKeys<T>;
}

/**
 * Type for custom key mapping function
 */
export type KeyMappingFunction = (key: string) => string;

/**
 * Convert object keys using a custom mapping function
 */
export function convertObjectKeysWithMapper<T extends Record<string, unknown>>(
  obj: T,
  mapper: KeyMappingFunction
): ConvertedKeys<T> {
  const result: Record<string, unknown> = {};

  Object.entries(obj).forEach(([key, value]) => {
    const newKey = mapper(key);
    result[newKey] = value;
  });

  return result as ConvertedKeys<T>;
}

/**
 * Configuration interface for case formatting
 */
export interface CaseFormatConfig {
  /** Format for URL parameters */
  urlFormat?: CaseFormat;
  /** Format for API parameters */
  apiFormat?: CaseFormat;
  /** Custom key mapping function (overrides format settings) */
  keyMapper?: KeyMappingFunction;
}

/**
 * Default case format configuration
 */
export const DEFAULT_CASE_CONFIG: Required<CaseFormatConfig> = {
  urlFormat: 'camelCase',
  apiFormat: 'snake_case',
  keyMapper: (key: string) => key, // No transformation by default
};

// ** import types
import type { ColumnDef } from "@tanstack/react-table";

/**
 * Type guard to check if column has id and size
 */
function hasIdAndSize<TData>(
  column: ColumnDef<TData, unknown>
): column is ColumnDef<TData, unknown> & { id: string; size: number } {
  return (
    'id' in column &&
    typeof column.id === 'string' &&
    'size' in column &&
    typeof column.size === 'number'
  );
}

/**
 * Type guard to check if column has accessorKey and size
 */
function hasAccessorKeyAndSize<TData>(
  column: ColumnDef<TData, unknown>
): column is ColumnDef<TData, unknown> & { accessorKey: string; size: number } {
  return (
    'accessorKey' in column &&
    typeof column.accessorKey === 'string' &&
    'size' in column &&
    typeof column.size === 'number'
  );
}

/**
 * Extract default column sizes from column definitions
 */
export function extractDefaultColumnSizes<TData>(
  columns: ColumnDef<TData, unknown>[]
): Record<string, number> {
  const defaultSizing: Record<string, number> = {};

  columns.forEach((column) => {
    if (hasIdAndSize(column)) {
      defaultSizing[column.id] = column.size;
    } else if (hasAccessorKeyAndSize(column)) {
      defaultSizing[column.accessorKey] = column.size;
    }
  });

  return defaultSizing;
}

/**
 * Validate parsed column sizing object
 */
function isValidColumnSizing(value: unknown): value is Record<string, number> {
  if (!value || typeof value !== 'object') {
    return false;
  }

  return Object.values(value as Record<string, unknown>).every(
    (size) =>
      typeof size === 'number' && !Number.isNaN(size) && size > 0
  );
}

/**
 * Initialize column sizes from localStorage or defaults
 */
export function initializeColumnSizes<TData>(
  columns: ColumnDef<TData, unknown>[],
  tableId: string,
  setColumnSizing: (sizes: Record<string, number>) => void
): void {
  // Only proceed if we have columns to work with
  if (columns.length === 0) return;

  // Extract default sizes from column definitions
  const defaultSizing = extractDefaultColumnSizes(columns);

  // Only set if we have sizes to apply
  if (Object.keys(defaultSizing).length === 0) return;

  // Check localStorage first
  try {
    const savedSizing = localStorage.getItem(`table-column-sizing-${tableId}`);
    if (!savedSizing) {
      // Only apply defaults if no saved sizing exists
      setColumnSizing(defaultSizing);
    } else {
      // Parse saved sizing
      const parsedSizing = JSON.parse(savedSizing);

      // Validate the parsed sizing
      if (isValidColumnSizing(parsedSizing)) {
        setColumnSizing(parsedSizing);
      } else {
        console.warn('Invalid column sizing format in localStorage, using defaults');
        setColumnSizing(defaultSizing);
      }
    }
  } catch (error) {
    // If localStorage fails, apply defaults
    console.warn('Failed to load saved column sizes. Using defaults.', error);
    setColumnSizing(defaultSizing);
  }
}

/**
 * Track column resizing state in document body for styling purposes
 */
export function trackColumnResizing(
  isResizing: boolean,
  attribute = 'data-resizing'
): void {
  if (isResizing) {
    document.body.setAttribute(attribute, 'true');
  } else {
    document.body.removeAttribute(attribute);
  }
}

/**
 * Clean up column resizing tracking when component unmounts
 */
export function cleanupColumnResizing(
  attribute = 'data-resizing'
): void {
  document.body.removeAttribute(attribute);
}

import { useState, useMemo, useCallback } from "react";
import { useUrlState } from "./url-state";

/**
 * Type for the setState function that might return a Promise
 */
type SetStateWithPromise<T> = (value: T | ((prevValue: T) => T)) => Promise<URLSearchParams> | undefined;

/**
 * Creates a state hook that can conditionally use URL state or regular React state
 * based on a configuration flag
 * 
 * @param enableUrlState - Whether to use URL state (true) or regular React state (false)
 * @returns A hook function that behaves like useState but with conditional URL persistence
 */
export function createConditionalStateHook(enableUrlState: boolean) {
  return function useConditionalState<T>(
    key: string, 
    defaultValue: T, 
    options: {
      serialize?: (value: T) => string;
      deserialize?: (value: string) => T;
    } = {}
  ): readonly [T, SetStateWithPromise<T>] {
    // Always call both hooks to satisfy React hooks rules
    const [regularState, setRegularState] = useState<T>(defaultValue);
    const [urlState, setUrlState] = useUrlState<T>(key, defaultValue, {
      ...options,
      enabled: enableUrlState,
    });

    // Create a compatible setState function for regular state that matches the SetStateWithPromise signature
    const setRegularStateWrapper = useCallback((valueOrUpdater: T | ((prevValue: T) => T)) => {
      setRegularState(valueOrUpdater);
      return undefined; // Return undefined instead of void to match the type
    }, []);

    // Return the appropriate state and setter based on config
    return useMemo(() => {
      if (enableUrlState) {
        return [urlState, setUrlState] as const;
      }

      return [regularState, setRegularStateWrapper] as const;
    }, [enableUrlState, regularState, urlState, setUrlState, setRegularStateWrapper]);
  };
}

/**
 * Format date to YYYY-MM-DD
 */
export function formatDate(date: Date): string {
  // Use local date components instead of UTC conversion
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0'); // getMonth() is 0-based
  const day = String(date.getDate()).padStart(2, '0');

  const result = `${year}-${month}-${day}`;

  return result;
}

import { ensureUrlStateHistoryPatched } from "./history-sync";

type TypedArray = Int8Array | Uint8Array | Uint8ClampedArray | Int16Array | Uint16Array | Int32Array | Uint32Array | Float32Array | Float64Array;

// Define a type for comparing values that can handle most common types
type Comparable = string | number | boolean | object | null | undefined | TypedArray | Date | RegExp | Set<unknown> | Map<unknown, unknown>;

// Simple LRU cache for frequent comparisons
class ComparisonCache {
  private cache = new Map<string, boolean>();
  private readonly maxSize = 100;

  getCacheKey(a: Comparable, b: Comparable): string | null {
    // Only cache for primitive values to avoid memory leaks
    if (typeof a !== 'object' && typeof b !== 'object' && a !== null && b !== null) {
      return `${typeof a}:${String(a)}|${typeof b}:${String(b)}`;
    }
    return null;
  }

  get(key: string): boolean | undefined {
    const value = this.cache.get(key);
    if (value !== undefined) {
      // Move to end to mark as recently used
      this.cache.delete(key);
      this.cache.set(key, value);
    }
    return value;
  }

  set(key: string, value: boolean): void {
    if (this.cache.size >= this.maxSize) {
      // Remove oldest entry
      const firstKey = this.cache.keys().next().value;
      if (firstKey !== undefined) {
        this.cache.delete(firstKey);
      }
    }
    this.cache.set(key, value);
  }
}

const comparisonCache = new ComparisonCache();

/**
 * Optimized deep equality check for objects and arrays
 * @param a First value to compare
 * @param b Second value to compare
 * @returns Boolean indicating if values are deeply equal
 */
export function isDeepEqual(a: Comparable, b: Comparable): boolean {
  // Check cache for primitive values first
  const cacheKey = comparisonCache.getCacheKey(a, b);
  if (cacheKey) {
    const cached = comparisonCache.get(cacheKey);
    if (cached !== undefined) {
      return cached;
    }
  }

  // Use a WeakMap to track object pairs we've compared to handle circular references
  const visited = new WeakMap<object, object>();

  const result = compare(a, b);

  // Cache the result for primitive values
  if (cacheKey) {
    comparisonCache.set(cacheKey, result);
  }

  return result;

  function compare(a: Comparable, b: Comparable): boolean {
    // Fast path for primitives and identical references
    if (a === b) return true;

    // Handle null/undefined
    if (a == null || b == null) return a === b;

    // Handle different types quickly
    const typeA = typeof a;
    const typeB = typeof b;
    if (typeA !== typeB) return false;

    // Fast non-recursive paths for common types
    if (typeA !== 'object') return false; // We already checked a === b for primitives

    // Handle special object types
    if (a instanceof Date) {
      return b instanceof Date && a.getTime() === b.getTime();
    }

    if (a instanceof RegExp) {
      return b instanceof RegExp && a.toString() === b.toString();
    }

    // Handle arrays more efficiently
    if (Array.isArray(a)) {
      if (!Array.isArray(b) || a.length !== b.length) return false;

      // For small arrays, use direct comparison
      if (a.length < 20) {
        for (let i = 0; i < a.length; i++) {
          if (!compare(a[i] as Comparable, b[i] as Comparable)) return false;
        }
        return true;
      }

      // For larger arrays, check if they contain only primitives
      const hasPrimitiveValues = a.every(item => typeof item !== 'object' || item === null);

      if (hasPrimitiveValues) {
        // Only sort arrays containing primitives
        const sortedA = [...a].sort();
        const sortedB = [...b].sort();

        // Quick comparison of primitives
        for (let i = 0; i < sortedA.length; i++) {
          if (sortedA[i] !== sortedB[i]) return false;
        }
      }

      // Compare actual positions for all arrays
      for (let i = 0; i < a.length; i++) {
        if (!compare(a[i] as Comparable, b[i] as Comparable)) return false;
      }

      return true;
    }

    // Special handling for Set
    if (a instanceof Set) {
      if (!(b instanceof Set) || a.size !== b.size) return false;

      // Convert to arrays and compare
      return compare([...a] as Comparable, [...b] as Comparable);
    }

    // Special handling for Map
    if (a instanceof Map) {
      if (!(b instanceof Map) || a.size !== b.size) return false;

      for (const [key, val] of a.entries()) {
        if (!b.has(key) || !compare(val as Comparable, b.get(key) as Comparable)) return false;
      }

      return true;
    }

    // Handle typed arrays
    if (ArrayBuffer.isView(a)) {
      if (!ArrayBuffer.isView(b) || (a as TypedArray).length !== (b as TypedArray).length) return false;

      // Use fast native comparison for TypedArrays
      if (a instanceof Uint8Array && b instanceof Uint8Array) {
        for (let i = 0; i < a.length; i++) {
          if (a[i] !== b[i]) return false;
        }
        return true;
      }

      // For other typed arrays
      return compare(Array.from(a as TypedArray), Array.from(b as TypedArray));
    }

    // Handle plain objects with circular reference detection
    if (a.constructor === Object && b.constructor === Object) {
      // Check for circular references
      if (visited.has(a as object)) {
        return visited.get(a as object) === b;
      }

      visited.set(a as object, b as object);

      const keysA = Object.keys(a);
      const keysB = Object.keys(b);

      // Quick length check
      if (keysA.length !== keysB.length) return false;

      // Sort keys for faster comparison
      keysA.sort();
      keysB.sort();

      // Compare keys first (much faster than comparing values)
      for (let i = 0; i < keysA.length; i++) {
        if (keysA[i] !== keysB[i]) return false;
      }

      // Compare values
      for (const key of keysA) {
        if (!compare((a as Record<string, unknown>)[key] as Comparable, (b as Record<string, unknown>)[key] as Comparable)) return false;
      }

      return true;
    }

    // If we get here, we're dealing with different object types or custom classes
    // First check if the objects have the same constructor
    if (a.constructor !== b.constructor) return false;

    // For custom classes, fall back to comparing properties
    const keysA = Object.keys(a);
    const keysB = Object.keys(b);

    if (keysA.length !== keysB.length) return false;

    for (const key of keysA) {
      if (!Object.prototype.hasOwnProperty.call(b, key)) return false;
      if (!compare((a as Record<string, unknown>)[key] as Comparable, (b as Record<string, unknown>)[key] as Comparable)) return false;
    }

    return true;
  }
}

/**
 * Memoizes the result of a function based on its arguments
 * This helps prevent redundant expensive operations
 */
export function memoize<T>(fn: (...args: unknown[]) => T): (...args: unknown[]) => T {
  const cache = new Map<string, T>();

  return (...args: unknown[]): T => {
    const key = JSON.stringify(args);

    if (cache.has(key)) {
      const cachedValue = cache.get(key);
      return cachedValue !== undefined ? cachedValue : fn(...args);
    }

    const result = fn(...args);
    cache.set(key, result);

    return result;
  };
}

/**
 * Creates a debounced function that delays invoking func until after wait milliseconds
 * @param func The function to debounce
 * @param wait The number of milliseconds to delay
 */
export function debounce<T extends (...args: unknown[]) => unknown>(
  func: T,
  wait: number
): (...args: Parameters<T>) => void {
  let timeout: ReturnType<typeof setTimeout> | null = null;

  return (...args: Parameters<T>) => {
    const later = () => {
      timeout = null;
      func(...args);
    };

    if (timeout) clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
}

/**
 * Reset URL parameters by removing all query parameters
 * @param pathname Optional pathname override (defaults to current `window.location.pathname`)
 */
export function resetUrlState(pathname?: string): void {
  if (typeof window === "undefined") return;

  // Make sure `history.replaceState` emits our url-state event.
  ensureUrlStateHistoryPatched();

  const nextPathname = pathname ?? window.location.pathname;
  const hash = window.location.hash;
  const nextUrl = `${nextPathname}${hash}`;

  window.history.replaceState(window.history.state, "", nextUrl);
}

import { toast } from "sonner";
import ExcelJS from "exceljs";


// Generic type for exportable data - should have string keys and values that can be converted to string
// Allow arrays for hierarchical data (subRows)
export type ExportableData = Record<string, string | number | boolean | null | undefined | any[]>;

/**
 * Flatten hierarchical data for export
 */
export function flattenHierarchicalData<T extends ExportableData>(
  data: T[],
  subRowsField: string = 'subRows',
  includeDepth: boolean = false
): T[] {
  const flattened: T[] = [];

  const flatten = (items: T[], depth: number = 0) => {
    items.forEach((item) => {
      const { [subRowsField]: subRows, ...itemData } = item as any;
      flattened.push(
        includeDepth
          ? ({ ...itemData, _depth: depth } as T)
          : (itemData as T)
      );

      if (subRows && Array.isArray(subRows) && subRows.length > 0) {
        flatten(subRows, depth + 1);
      }
    });
  };

  flatten(data);
  return flattened;
}

/**
 * Export only parent rows (remove subrows)
 */
export function exportParentRowsOnly<T extends ExportableData>(
  data: T[],
  subRowsField: string = 'subRows'
): T[] {
  return data.map((item) => {
    const { [subRowsField]: _, ...parentData } = item as any;
    return parentData as T;
  });
}

// Type for transformation function that developers can provide
export type DataTransformFunction<T extends ExportableData> = (row: T) => ExportableData;

/**
 * Convert array of objects to CSV string
 */
function convertToCSV<T extends ExportableData>(
  data: T[], 
  headers: string[], 
  columnMapping?: Record<string, string>,
  transformFunction?: DataTransformFunction<T>
): string {
  if (data.length === 0) {
    throw new Error("No data to export");
  }

  // Create CSV header row with column mapping if provided
  let csvContent = "";

  if (columnMapping) {
    // Use column mapping for header names
    const headerRow = headers.map(header => {
      const mappedHeader = columnMapping[header] || header;
      // Escape quotes and wrap in quotes if contains comma
      return mappedHeader.includes(",") || mappedHeader.includes('"')
        ? `"${mappedHeader.replace(/"/g, '""')}"`
        : mappedHeader;
    });
    csvContent = `${headerRow.join(",")}\n`;
  } else {
    // Use original headers
    csvContent = `${headers.join(",")}\n`;
  }

  // Add data rows
  for (const item of data) {
    // Apply transformation function if provided
    const transformedItem = transformFunction ? transformFunction(item) : item;

    const row = headers.map(header => {
      // Get the value for this header from the transformed item
      const value = transformedItem[header];

      // Convert all values to string and properly escape for CSV
      const cellValue = value === null || value === undefined ? "" : String(value);
      // Escape quotes and wrap in quotes if contains comma
      const escapedValue = cellValue.includes(",") || cellValue.includes('"')
        ? `"${cellValue.replace(/"/g, '""')}"`
        : cellValue;

      return escapedValue;
    });

    csvContent += `${row.join(",")}\n`;
  }

  return csvContent;
}

/**
 * Download blob as file
 */
function downloadFile(blob: Blob, filename: string) {
  const link = document.createElement("a");
  const url = URL.createObjectURL(blob);

  link.setAttribute("href", url);
  link.setAttribute("download", filename);
  link.style.visibility = "hidden";

  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
}

/**
 * Export data to CSV file
 */
export function exportToCSV<T extends ExportableData>(
  data: T[],
  filename: string,
  headers: string[] = Object.keys(data[0] || {}),
  columnMapping?: Record<string, string>,
  transformFunction?: DataTransformFunction<T>
): boolean {
  if (data.length === 0) {
    console.error("No data to export");
    return false;
  }

  try {
    // Apply transformation function first if provided, then filter data to only include specified headers
    const processedData = data.map(item => {
      // Apply transformation function if provided
      const transformedItem = transformFunction ? transformFunction(item) : item;

      // Filter to only include specified headers
      const filteredItem: ExportableData = {};
      for (const header of headers) {
        if (header in transformedItem) {
          filteredItem[header] = transformedItem[header];
        }
      }
      return filteredItem;
    });

    const csvContent = convertToCSV(processedData, headers, columnMapping);
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    downloadFile(blob, `${filename}.csv`);
    return true;
  } catch (error) {
    console.error("Error creating CSV:", error);
    return false;
  }
}

/**
 * Export data to Excel file using ExcelJS (secure alternative to xlsx)
 */
export async function exportToExcel<T extends ExportableData>(
  data: T[],
  filename: string,
  columnMapping?: Record<string, string>,
  columnWidths?: Array<{ wch: number }>,
  headers?: string[],
  transformFunction?: DataTransformFunction<T>
): Promise<boolean> {
  if (data.length === 0) {
    console.error("No data to export");
    return false;
  }

  try {
    // If no column mapping is provided, create one from the data keys
    const mapping = columnMapping ||
      Object.keys(data[0] || {}).reduce((acc, key) => {
        acc[key] = key.charAt(0).toUpperCase() + key.slice(1).replace(/_/g, ' ');
        return acc;
      }, {} as Record<string, string>);

    // Create a new workbook and worksheet
    const workbook = new ExcelJS.Workbook();
    const worksheet = workbook.addWorksheet("Data");

    // Determine columns to export
    const columnsToExport = headers || Object.keys(mapping);

    // Set up columns with headers and widths
    worksheet.columns = columnsToExport.map((key, index) => ({
      header: mapping[key] || key,
      key: key,
      width: columnWidths?.[index]?.wch || 15, // Default width of 15 if not specified
    }));

    // Apply transformation function first if provided, then add data rows
    data.forEach(item => {
      const transformedItem = transformFunction ? transformFunction(item) : item;

      const row: Record<string, any> = {};
      for (const key of columnsToExport) {
        if (key in transformedItem) {
          row[key] = transformedItem[key];
        }
      }
      worksheet.addRow(row);
    });

    // Style the header row
    worksheet.getRow(1).font = { bold: true };
    worksheet.getRow(1).fill = {
      type: 'pattern',
      pattern: 'solid',
      fgColor: { argb: 'FFE0E0E0' }
    };

    // Generate Excel file buffer
    const buffer = await workbook.xlsx.writeBuffer();

    // Create blob and download
    const blob = new Blob([buffer], {
      type: "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"
    });

    downloadFile(blob, `${filename}.xlsx`);
    return true;
  } catch (error) {
    console.error("Error creating Excel file:", error);
    return false;
  }
}

/**
 * Unified export function that handles loading states and error handling
 */
export async function exportData<T extends ExportableData>(
  type: "csv" | "excel",
  getData: () => Promise<T[]>,
  onLoadingStart?: () => void,
  onLoadingEnd?: () => void,
  options?: {
    headers?: string[];
    columnMapping?: Record<string, string>;
    columnWidths?: Array<{ wch: number }>;
    entityName?: string;
    transformFunction?: DataTransformFunction<T>;
  }
): Promise<boolean> {
  // Use a consistent toast ID to ensure only one toast is shown at a time
  const TOAST_ID = "export-data-toast";

  try {
    // Start loading
    if (onLoadingStart) onLoadingStart();

    // Show toast for long operations using consistent ID
    toast.loading("Preparing export...", {
      description: "Fetching data for export...",
      id: TOAST_ID
    });

    // Get the data
    const exportData = await getData();

    // Update the same toast for processing
    toast.loading("Processing data...", {
      description: "Generating export file...",
      id: TOAST_ID
    });

    if (exportData.length === 0) {
      toast.error("Export failed", {
        description: "No data available to export.",
        id: TOAST_ID
      });
      return false;
    }

    // Get entity name for display in notifications
    const entityName = options?.entityName || "items";

    // Generate timestamp for filename
    const timestamp = new Date().toISOString().replace(/[:.]/g, "-");
    const filename = `${entityName}-export-${timestamp}`;

    // Export based on type
    let success = false;
    if (type === "csv") {
      success = exportToCSV(
        exportData,
        filename,
        options?.headers,
        options?.columnMapping,
        options?.transformFunction
      );
      if (success) {
        toast.success("Export successful", {
          description: `Exported ${exportData.length} ${entityName} to CSV.`,
          id: TOAST_ID
        });
      }
    } else {
      success = await exportToExcel(
        exportData,
        filename,
        options?.columnMapping,
        options?.columnWidths,
        options?.headers,
        options?.transformFunction
      );
      if (success) {
        toast.success("Export successful", {
          description: `Exported ${exportData.length} ${entityName} to Excel.`,
          id: TOAST_ID
        });
      }
    }

    return success;
  } catch (error) {
    console.error("Error exporting data:", error);

    toast.error("Export failed", {
      description: "There was a problem exporting the data. Please try again.",
      id: TOAST_ID
    });
    return false;
  } finally {
    // End loading regardless of result
    if (onLoadingEnd) onLoadingEnd();
  }
}

