import type { Table } from "@tanstack/react-table";
import type { KeyboardEvent } from "react";

/**
 * Creates a keyboard navigation handler for data tables
 * 
 * Supports:
 * - Space to toggle selection
 * - Enter to activate/view a row
 */
export function createKeyboardNavigationHandler<TData>(
  table: Table<TData>,
  onRowActivate?: (row: TData, rowIndex: number) => void
) {
  return (e: KeyboardEvent) => {
    // If the key is Space or Enter and we're not in an input/button, handle row selection/activation
    if ((e.key === " " || e.key === "Enter") && 
        !(e.target as HTMLElement).matches('input, button, [role="button"], [contenteditable="true"]')) {
      // Prevent default behavior
      e.preventDefault();

      // Find the focused row or cell
      const focusedElement = document.activeElement;
      if (focusedElement && (
          focusedElement.getAttribute('role') === 'row' || 
          focusedElement.getAttribute('role') === 'gridcell'
      )) {
        // Find the closest row
        const rowElement = focusedElement.getAttribute('role') === 'row' 
          ? focusedElement 
          : focusedElement.closest('[role="row"]');

        if (rowElement) {
          // Get the row index from the data-row-index attribute or the row id
          const rowId = rowElement.getAttribute('data-row-index') || rowElement.id;
          if (rowId) {
            // Find the row by index and toggle its selection
            const rowIndex = Number.parseInt(rowId.replace(/^row-/, ''), 10);
            const row = table.getRowModel().rows[rowIndex];
            if (row) {
              if (e.key === " ") {
                // Space toggles selection
                row.toggleSelected();
              } else if (e.key === "Enter" && onRowActivate) {
                // Enter activates the row
                onRowActivate(row.original, rowIndex);
              }
            }
          }
        }
      }
    }
  };
}

/**
 * Preprocess search terms before sending to API
 * 
 * - Trims whitespace
 * - Removes excessive spaces
 * - Sanitizes potentially harmful characters
 * - Returns empty string for invalid inputs
 */
export function preprocessSearch(searchTerm: string): string {
  if (!searchTerm) return "";

  // Trim whitespace
  let processed = searchTerm.trim();

  // Remove excessive whitespace within the search term
  processed = processed.replace(/\s+/g, ' ');

  // Check for minimum length after processing (e.g., if it's just spaces)
  if (processed.length < 1) return "";

  // Comprehensive sanitization for XSS protection
  // Remove script tags and potentially harmful HTML/JS patterns
  processed = processed.replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '');
  processed = processed.replace(/javascript:/gi, '');
  processed = processed.replace(/on\w+\s*=/gi, '');

  // HTML entity encoding for common XSS vectors
  processed = processed.replace(/&/g, '&amp;')
                     .replace(/</g, '&lt;')
                     .replace(/>/g, '&gt;')
                     .replace(/"/g, '&quot;')
                     .replace(/'/g, '&#x27;')
                     .replace(/\//g, '&#x2F;');

  return processed;
}

/**
 * Table configuration options
 * This file provides centralized configuration for the data table features
 */
export interface TableConfig {
  // Enable/disable row selection
  enableRowSelection: boolean;

  // Enable/disable keyboard navigation
  enableKeyboardNavigation: boolean;

  // Enable/disable clicking a row to select it
  enableClickRowSelect: boolean;

  // Enable/disable pagination
  enablePagination: boolean;

  // Enable/disable search
  enableSearch: boolean;

  // Enable/disable column filters
  enableColumnFilters: boolean;

  // Enable/disable date range filter
  enableDateFilter: boolean;

  // Enable/disable column visibility options
  enableColumnVisibility: boolean;

  // Enable/disable data export
  enableExport: boolean;

  // Enable/disable URL state persistence
  enableUrlState: boolean;

  // Enable/disable column resizing
  enableColumnResizing: boolean;

  // Enable/disable toolbar
  enableToolbar: boolean;

  // Control the size of buttons and inputs throughout the table
  // sm: small, default: standard, lg: large
  size: 'sm' | 'default' | 'lg';

  // Unique ID for storing column sizing in localStorage
  // This allows multiple tables to have independent sizing states
  columnResizingTableId?: string;

  // Custom placeholder text for search input
  // If not provided, defaults to "Search {entityName}..."
  searchPlaceholder?: string;

  // Allow exporting new columns created by transform function
  // When true (default): Export includes visible columns + new columns from transform function
  // When false: Export only includes visible columns (hidden columns always excluded)
  // Note: Hidden columns are ALWAYS excluded regardless of this setting
  allowExportNewColumns: boolean;

  // Default sort column (should match column accessorKey exactly)
  // Use the same case format as your API response (snake_case or camelCase)
  defaultSortBy?: string;

  // Default sort direction
  defaultSortOrder?: 'asc' | 'desc';
}

// Default configuration
const defaultConfig: TableConfig = {
  enableRowSelection: true,      // Row selection enabled by default
  enableKeyboardNavigation: false, // Keyboard navigation disabled by default
  enableClickRowSelect: false,    // Clicking row to select disabled by default
  enablePagination: true,         // Pagination enabled by default
  enableSearch: true,             // Search enabled by default
  enableColumnFilters: true,      // Column filters enabled by default
  enableDateFilter: true,         // Date filter enabled by default
  enableColumnVisibility: true,   // Column visibility options enabled by default
  enableExport: true,             // Data export enabled by default
  enableUrlState: true,           // URL state persistence enabled by default
  enableColumnResizing: true,     // Column resizing enabled by default
  enableToolbar: true,            // Toolbar enabled by default
  size: 'default',                // Default size for buttons and inputs
  columnResizingTableId: undefined, // No table ID by default
  searchPlaceholder: undefined,   // No custom search placeholder by default
  allowExportNewColumns: true,    // Allow new columns from transform function by default
  defaultSortBy: undefined,       // No default sort column - must be provided in config
  defaultSortOrder: 'desc',       // Default to descending order
};

/**
 * Hook to provide table configuration
 * Allows overriding default configuration
 */
export function useTableConfig(overrideConfig?: Partial<TableConfig>): TableConfig {
  // Merge default config with any overrides
  const config = { ...defaultConfig, ...overrideConfig };

  return config;
}

import type { SortingState, ColumnFiltersState, VisibilityState, PaginationState, ColumnSizingState } from "@tanstack/react-table";

type SortingUpdater = (prev: SortingState) => SortingState;
type StatePromise = Promise<URLSearchParams> | undefined;
type SetStateFunction<T> = (value: T | ((prev: T) => T)) => StatePromise;

/**
 * Handler for sorting changes in a data table
 * Ensures that sortBy is updated before sortOrder for proper synchronization
 */
export function createSortingHandler(
  setSortBy: SetStateFunction<string>,
  setSortOrder: SetStateFunction<"asc" | "desc">
) {
  return (updaterOrValue: SortingState | SortingUpdater): void => {
    // Handle both direct values and updater functions
    const newSorting = typeof updaterOrValue === 'function'
      ? updaterOrValue([])
      : updaterOrValue;

    // Only update if there's a valid sorting instruction
    if (newSorting.length > 0) {
      const columnId = newSorting[0].id;
      const direction = newSorting[0].desc ? "desc" : "asc";

      // Important: Sequential updates to ensure proper synchronization
      // First update the column id
      const sortByResult = setSortBy(columnId);

      if (sortByResult instanceof Promise) {
        // If using URL state (Promise-based), chain the updates
        sortByResult.then(() => {
          // Then set the sort direction
          setSortOrder(direction);
        });
      } else {
        // If using regular state (non-Promise), just update sequentially
        setSortOrder(direction);
      }
    }
    // Don't reset to defaults when sort is explicitly cleared
    // This prevents overriding user selections with default values
  };
}

/**
 * Handler for column filters changes in a data table
 */
export function createColumnFiltersHandler(
  setColumnFilters: SetStateFunction<ColumnFiltersState>
) {
  return (updaterOrValue: ColumnFiltersState | ((prev: ColumnFiltersState) => ColumnFiltersState)) => {
    // Pass through to setColumnFilters (which handles updater functions)
    setColumnFilters(updaterOrValue);
  };
}

/**
 * Handler for column visibility changes in a data table
 */
export function createColumnVisibilityHandler(
  setColumnVisibility: SetStateFunction<VisibilityState>
) {
  return (updaterOrValue: VisibilityState | ((prev: VisibilityState) => VisibilityState)) => {
    // Pass through to setColumnVisibility (which handles updater functions)
    setColumnVisibility(updaterOrValue);
  };
}

/**
 * Handler for pagination changes in a data table
 */
export function createPaginationHandler(
  setPage: SetStateFunction<number>,
  setPageSize: SetStateFunction<number>,
  currentPage: number,
  currentPageSize: number
) {
  return (updaterOrValue: PaginationState | ((prev: PaginationState) => PaginationState)) => {
    // Handle both direct values and updater functions
    const newPagination = typeof updaterOrValue === 'function'
      ? updaterOrValue({ pageIndex: currentPage - 1, pageSize: currentPageSize })
      : updaterOrValue;

    setPage(newPagination.pageIndex + 1);
    setPageSize(newPagination.pageSize);
  };
}

/**
 * Handler for column sizing changes in a data table
 */
export function createColumnSizingHandler(
  setColumnSizing: SetStateFunction<ColumnSizingState>,
  columnSizing: ColumnSizingState
) {
  return (updaterOrValue: ColumnSizingState | ((prev: ColumnSizingState) => ColumnSizingState)) => {
    // Handle both direct values and updater functions
    const newSizing = typeof updaterOrValue === 'function'
      ? updaterOrValue(columnSizing)
      : updaterOrValue;
    setColumnSizing(newSizing);
  };
}

/**
 * Convert URL sorting parameters to TanStack Table SortingState
 */
export function createSortingState(
  sortBy?: string, 
  sortOrder?: "asc" | "desc"
): SortingState {
  return sortBy && sortOrder
    ? [{ id: sortBy, desc: sortOrder === "desc" }]
    : [];
}

// ** import core packages
import { useCallback, useEffect, useMemo, useRef, useState } from "react";

// ** import utils
import { isDeepEqual } from "./deep-utils";
import { DATA_TABLE_URL_STATE_EVENT } from "./url-events";
import { ensureUrlStateHistoryPatched } from "./history-sync";

function canUseDOM(): boolean {
  return typeof window !== "undefined" && typeof window.location !== "undefined";
}

function getCurrentSearchParams(): URLSearchParams {
  if (!canUseDOM()) return new URLSearchParams();
  return new URLSearchParams(window.location.search);
}

function replaceCurrentUrlSearchParams(params: URLSearchParams): void {
  if (!canUseDOM()) return;

  // Ensure we get a signal for ANY URL updates (router, other components, etc).
  ensureUrlStateHistoryPatched();

  const newParamsString = params.toString();
  const pathname = window.location.pathname;
  const hash = window.location.hash;
  const nextUrl = `${pathname}${newParamsString ? `?${newParamsString}` : ""}${hash}`;
  const currentUrl = `${window.location.pathname}${window.location.search}${window.location.hash}`;

  // Avoid doing work (and triggering re-renders) if the URL would not change.
  if (nextUrl === currentUrl) return;

  window.history.replaceState(window.history.state, "", nextUrl);
}

// Batch update state management with instance tracking to prevent race conditions
interface BatchUpdateState {
  isInBatchUpdate: boolean;
  batchId: number;
  pendingUpdates: Map<string, PendingUpdateEntry>;
}

interface PendingUpdateEntry<T = unknown> {
  value: T;
  defaultValue: T;
  serialize: (value: T) => string;
  areEqual: (a: T, b: T) => boolean;
}

// Global state with proper isolation
const batchUpdateState: BatchUpdateState = {
  isInBatchUpdate: false,
  batchId: 0,
  pendingUpdates: new Map<string, PendingUpdateEntry>(),
};

// Timeout to prevent stuck batch updates
let batchTimeoutId: ReturnType<typeof setTimeout> | null = null;
const BATCH_TIMEOUT = 100; // 100ms timeout for batch updates

/**
 * Custom hook for managing URL-based state
 * This provides a simpler approach for storing state in URL params
 */
export function useUrlState<T>(
  key: string,
  defaultValue: T,
  options: {
    serialize?: (value: T) => string;
    deserialize?: (value: string) => T;
    enabled?: boolean;
  } = {}
) {
  const enabled = options.enabled ?? true;

  const [searchParams, setSearchParams] = useState<URLSearchParams>(() =>
    enabled ? getCurrentSearchParams() : new URLSearchParams()
  );

  // Keep searchParams in sync with the real URL.
  // - popstate: back/forward navigation
  // - DATA_TABLE_URL_STATE_EVENT: our own URL updates
  useEffect(() => {
    if (!enabled || !canUseDOM()) return;

    // Patch once so we observe `pushState`/`replaceState` (e.g. Next router).
    ensureUrlStateHistoryPatched();

    const handleUrlChange = () => {
      const nextParams = getCurrentSearchParams();
      const nextString = nextParams.toString();
      setSearchParams((prev) => (prev.toString() === nextString ? prev : nextParams));
    };

    window.addEventListener("popstate", handleUrlChange);
    window.addEventListener(DATA_TABLE_URL_STATE_EVENT, handleUrlChange);

    // Ensure we start from the current URL in case we were mounted before the router
    // finished updating the address bar.
    handleUrlChange();

    return () => {
      window.removeEventListener("popstate", handleUrlChange);
      window.removeEventListener(DATA_TABLE_URL_STATE_EVENT, handleUrlChange);
    };
  }, [enabled]);

  // Use ref to track if we're currently updating URL
  // This prevents recursive updates when router changes trigger effects
  const isUpdatingUrl = useRef(false);

  // Add a reference to track the last value we updated to
  const lastSetValue = useRef<T>(defaultValue);

  // Custom serialization/deserialization functions
  const serialize =
    options.serialize ||
    ((value: T) =>
      typeof value === "object" ? JSON.stringify(value) : String(value));

  const deserialize =
    options.deserialize ||
    ((value: string) => {
      try {
        if (typeof defaultValue === "number") {
          const num = Number(value);
          // Check if the parsed value is a valid number
          if (Number.isNaN(num)) return defaultValue;
          return num as unknown as T;
        }

        if (typeof defaultValue === "boolean") {
          return (value === "true") as unknown as T;
        }

        if (typeof defaultValue === "object") {
          try {
            const parsed = JSON.parse(value) as T;
            // Validate the structure matches what we expect
            if (parsed && typeof parsed === "object") {
              // For dateRange, check if it has the expected properties
              if (key === "dateRange") {
                const dateRange = parsed as {
                  from_date?: string;
                  to_date?: string;
                };
                if (!dateRange.from_date || !dateRange.to_date) {
                  console.warn(`Invalid dateRange format in URL: ${value}`);
                  return defaultValue;
                }
              }
              return parsed;
            }
            return defaultValue;
          } catch (e) {
            console.warn(`Error parsing JSON from URL parameter ${key}: ${e}`);
            return defaultValue;
          }
        }

        return value as unknown as T;
      } catch (e) {
        console.warn(`Error deserializing URL parameter ${key}: ${e}`);
        return defaultValue;
      }
    });

  // Get the initial value from URL or use default
  const getValueFromUrl = useCallback(() => {
    if (!enabled) return defaultValue;

    // Check if we have a pending update for this key that hasn't been applied yet
    if (batchUpdateState.pendingUpdates.has(key)) {
      const pendingUpdate = batchUpdateState.pendingUpdates.get(key);
      if (pendingUpdate && typeof pendingUpdate.value !== 'undefined') {
        return pendingUpdate.value as T;
      }
    }

    const paramValue = searchParams.get(key);
    if (paramValue === null) {
      return defaultValue;
    }

    // Special handling for search parameter to decode URL-encoded spaces
    if (key === "search" && typeof defaultValue === "string") {
      return decodeURIComponent(paramValue) as unknown as T;
    }

    return deserialize(paramValue);
  }, [enabled, searchParams, key, deserialize, defaultValue]);

  // State to store the current value
  const [value, setValue] = useState<T>(getValueFromUrl);

  // Track the previous search params to avoid unnecessary updates
  const prevSearchParamsRef = useRef<URLSearchParams | null>(null);

  // Deep compare objects/arrays before updating state
  const areEqual = useMemo(() => {
    return (a: T, b: T): boolean => {
      if (typeof a === "object" && typeof b === "object") {
        return isDeepEqual(a, b);
      }
      return a === b;
    };
  }, []);

  // Keep a ref to track the current value to avoid dependency on the state variable
  const currentValueRef = useRef<T>(value);

  // Update currentValueRef whenever value changes
  useEffect(() => {
    currentValueRef.current = value;
  }, [value]);

  // Update state when URL changes, but only if we're not the ones changing it
  useEffect(() => {
    // Skip if we're the ones currently updating the URL
    if (isUpdatingUrl.current) {
      isUpdatingUrl.current = false;
      return;
    }

    // Check if searchParams actually changed
    const searchParamsString = searchParams.toString();
    if (
      prevSearchParamsRef.current &&
      prevSearchParamsRef.current.toString() === searchParamsString
    ) {
      return;
    }

    // Update the previous search params ref
    const newParams = new URLSearchParams(searchParamsString);
    prevSearchParamsRef.current = newParams;

    // Get the new value and update if different
    const newValue = getValueFromUrl();

    // Check if this is a value we just set ourselves
    // Using refs to track state without creating dependencies
    if (
      !areEqual(lastSetValue.current, newValue) && 
      !areEqual(currentValueRef.current, newValue)
    ) {
      // Prevent immediate re-triggering of this effect due to state update
      lastSetValue.current = newValue;
      setValue(newValue);
    } else if (
      batchUpdateState.pendingUpdates.has(key) &&
      areEqual(batchUpdateState.pendingUpdates.get(key)?.value as unknown as T, newValue)
    ) {
      // If our pending update has been applied, we can remove it from the map
      batchUpdateState.pendingUpdates.delete(key);
    }
  }, [searchParams, getValueFromUrl, key, areEqual]); // No dependency on value

  // Synchronously update URL now instead of waiting
  const updateUrlNow = useCallback(
    (params: URLSearchParams) => {
      if (!enabled) {
        isUpdatingUrl.current = false;
        return Promise.resolve(params);
      }

      // Update the URL immediately (framework-agnostic).
      replaceCurrentUrlSearchParams(params);
      isUpdatingUrl.current = false;

      // Return the params for Promise chaining
      return Promise.resolve(params);
    },
    [enabled]
  );

  // Update the URL when the state changes
  const updateValue = useCallback(
    (newValue: T | ((prevValue: T) => T)) => {
      const resolvedValue =
        typeof newValue === "function"
          ? (newValue as (prev: T) => T)(value)
          : newValue;

      if (!enabled) {
        setValue(resolvedValue);
        return Promise.resolve(new URLSearchParams());
      }

      // Skip update if value is the same (deep comparison for objects)
      if (areEqual(value, resolvedValue)) {
        return Promise.resolve(new URLSearchParams(searchParams.toString()));
      }

      // Save this value to our ref to prevent overrides
      lastSetValue.current = resolvedValue;

      // Store the value, defaultValue, serialize, and areEqual in the pending updates map
      batchUpdateState.pendingUpdates.set(key, {
        value: resolvedValue,
        defaultValue,
        serialize: serialize as (value: unknown) => string,
        areEqual: areEqual as (a: unknown, b: unknown) => boolean,
      });

      // Set state locally first for immediate UI response
      setValue(resolvedValue);

      // Set flag to prevent recursive updates
      isUpdatingUrl.current = true;

      // Handle pageSize and page relationship - ensure page is reset to 1 when pageSize changes
      if (key === "pageSize") {
        // If pageSize changes, "page" should be reset to 1.
        // We need to ensure this "page" entry has appropriate functions.
        // For now, assume standard defaults for "page" if it's not already managed by its own useUrlState.
        // A more robust solution might involve a shared registry or context for URL state configurations.
        const pageEntry: PendingUpdateEntry<number> = batchUpdateState.pendingUpdates.get("page") as PendingUpdateEntry<number> || {
          value: 1,
          defaultValue: 1, // Assuming default page is 1
          serialize: (v: number) => String(v),
          areEqual: (a: number, b: number) => a === b,
        };
        batchUpdateState.pendingUpdates.set("page", { ...pageEntry, value: 1 } as PendingUpdateEntry<unknown>);
      }

      // If we're in a batch update, delay URL change
      if (batchUpdateState.isInBatchUpdate) {
        return Promise.resolve(new URLSearchParams(searchParams.toString()));
      }

      // Start a batch update to collect multiple URL changes in the current event loop
      batchUpdateState.isInBatchUpdate = true;
      batchUpdateState.batchId++;
      const currentBatchId = batchUpdateState.batchId;

      // Clear any existing timeout
      if (batchTimeoutId) {
        clearTimeout(batchTimeoutId);
      }

      // Use microtask to batch all URL changes in the current event loop
      return new Promise<URLSearchParams>((resolve) => {
        const processBatch = () => {
          // Check if this batch is still valid (not superseded by a newer batch)
          if (currentBatchId !== batchUpdateState.batchId) {
            return;
          }
          // Start with the current search params as a base
          const params = new URLSearchParams(searchParams.toString());
          let pageSizeChangedInBatch = false;

          // Keep track if any sort parameters are in the current batch
          let sortByInBatch = false;
          let sortOrderInBatch = false;

          // Check if sortBy/sortOrder are already in the URL
          const sortByInURL = params.has("sortBy");
          const defaultSortOrder = "desc"; // Match the default from the component

          // First pass: identify which sort parameters are being updated
          for (const [updateKey, _] of batchUpdateState.pendingUpdates.entries()) {
            if (updateKey === "sortBy") sortByInBatch = true;
            if (updateKey === "sortOrder") sortOrderInBatch = true;
          }

          // Iterate over all pending updates and apply them to the params
          for (const [updateKey, entry] of batchUpdateState.pendingUpdates.entries()) {
            const {
              value: updateValue,
              defaultValue: entryDefaultValue,
              serialize: entrySerialize,
              areEqual: entryAreEqual,
            } = entry;

            // Special case: Always include sort-related parameters to ensure URL consistency
            if (updateKey === "sortBy") {
              // When setting sortBy, always include it in URL
              params.set(updateKey, entrySerialize(updateValue));

              // If sortOrder isn't being updated in this batch, ensure it's included
              if (!sortOrderInBatch) {
                // Get current sortOrder value from URL or use default
                const currentSortOrder = params.get("sortOrder") || defaultSortOrder;
                params.set("sortOrder", currentSortOrder);
              }
            } 
            else if (updateKey === "sortOrder") {
              // Always include sortOrder when sortBy is present (either in URL or in this batch)
              if (sortByInURL || sortByInBatch) {
                params.set(updateKey, entrySerialize(updateValue));
              }
              else if (entryAreEqual(updateValue, entryDefaultValue)) {
                params.delete(updateKey);
              }
              else {
                params.set(updateKey, entrySerialize(updateValue));
              }
            }
            else if (entryAreEqual(updateValue, entryDefaultValue)) {
              params.delete(updateKey);
            } 
            else {
              // Special handling for search parameter to preserve spaces
              if (updateKey === "search" && typeof updateValue === "string") {
                // Use encodeURIComponent to properly encode spaces as %20 instead of +
                params.set(updateKey, encodeURIComponent(updateValue));
              } else {
                params.set(updateKey, entrySerialize(updateValue));
              }
            }
            if (updateKey === "pageSize") {
              pageSizeChangedInBatch = true;
            }
          }

          // If pageSize was part of this batch update, ensure page is set to 1
          // This handles the case where "page" might have been set to something else
          // in the same batch, but a pageSize change should override it to 1.
          if (pageSizeChangedInBatch) {
            params.set("page", "1");
          }

          // Clear all pending updates as they've been processed
          batchUpdateState.pendingUpdates.clear();

          // End the batch update
          batchUpdateState.isInBatchUpdate = false;

          // Clear the timeout since we're processing now
          if (batchTimeoutId) {
            clearTimeout(batchTimeoutId);
            batchTimeoutId = null;
          }

          // Update the URL immediately and resolve
          updateUrlNow(params).then(resolve);
        };

        // Process batch in microtask
        queueMicrotask(processBatch);

        // Also set a timeout as fallback to prevent stuck batches
        batchTimeoutId = setTimeout(processBatch, BATCH_TIMEOUT);
      });
    },
    [
      enabled,
      searchParams,
      key,
      serialize,
      value,
      defaultValue,
      updateUrlNow,
      areEqual
    ]
  );

  return [value, updateValue] as const;
}

// Helper to convert a date object to YYYY-MM-DD format
export function formatDateForUrl(date: Date | undefined): string {
  if (!date) return "";
  return date.toISOString().split("T")[0];
}

// Helper to safely validate and parse date strings from URL
export function validateDateString(dateString: string): boolean {
  if (!dateString) return false;

  // Check if it's in YYYY-MM-DD format
  const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
  if (!dateRegex.test(dateString)) return false;

  // Check if it's a valid date
  const date = new Date(dateString);
  return !Number.isNaN(date.getTime());
}

// Helper to parse a YYYY-MM-DD string to a Date object
export function parseDateFromUrl(dateString: string): Date | undefined {
  if (!validateDateString(dateString)) return undefined;
  return new Date(dateString);
}

// Shared event name used to notify all url-state hooks that the URL changed.
// Keep this in a separate module to avoid circular deps between utils.
export const DATA_TABLE_URL_STATE_EVENT = "tnks:data-table:url-state";

import { DATA_TABLE_URL_STATE_EVENT } from "./url-events";

let patched = false;

function canUseDOM(): boolean {
  return (
    typeof window !== "undefined" &&
    typeof window.history !== "undefined" &&
    typeof window.dispatchEvent !== "undefined"
  );
}

function dispatchUrlStateEvent(): void {
  if (!canUseDOM()) return;

  // Next.js can call `history.pushState/replaceState` during `useInsertionEffect`.
  // Dispatching synchronously can trigger React state updates during that phase
  // (e.g. `useUrlState` listener), which React disallows.
  const dispatch = () => window.dispatchEvent(new Event(DATA_TABLE_URL_STATE_EVENT));

  if (typeof queueMicrotask === "function") {
    queueMicrotask(dispatch);
    return;
  }

  Promise.resolve().then(dispatch);
}

/**
 * Patch history APIs to emit a shared event when the URL changes.
 *
 * Notes:
 * - `popstate` does NOT fire for `history.pushState`/`replaceState`.
 * - Next.js `router.push`/`router.replace` commonly uses these APIs.
 * - We patch once globally and keep it installed for the app lifetime.
 */
export function ensureUrlStateHistoryPatched(): void {
  if (patched || !canUseDOM()) return;

  const originalPushState = window.history.pushState.bind(window.history);
  const originalReplaceState = window.history.replaceState.bind(window.history);

  window.history.pushState = ((...args: Parameters<History["pushState"]>) => {
    originalPushState(...args);
    dispatchUrlStateEvent();
  }) as History["pushState"];

  window.history.replaceState = ((
    ...args: Parameters<History["replaceState"]>
  ) => {
    originalReplaceState(...args);
    dispatchUrlStateEvent();
  }) as History["replaceState"];

  // Hash changes via `location.hash = ...` won't go through history methods.
  window.addEventListener("hashchange", dispatchUrlStateEvent);

  patched = true;
}

export function isUrlStateHistoryPatched(): boolean {
  return patched;
}
